scwin.btn_cancel_onclick = function() {
	var idx = SI0402G1.getModifiedIndex().length;

	if (idx > 0) {
		var str =com.strModifiedCnt(SI0402G1) + "의 저장하지 않은 데이터가 있습니다. 취소 하시겠습니까?";
		com.confirm(str, function(rtn) {
			if (rtn) {
				com.clearGrid(SI0402G1);
				com.searchGrid(SI0402G1, sub_SI0402G1R0);
				//routMap.drawRoute("map_SI0402", SI0402G1, SI0402G1.getFocusedRowIndex(), false);
			}
		});
	}

};

scwin.btn_save_onclick = function() {


	com.setSerialNumberToData(SI0402G1, "NODE_SN", "OLD_NODE_SN");

	var idx = SI0402G1.getModifiedIndex().length;

	if (idx == 0) {
		if(	(typeof str == "undefined") || (str.trim() == ""))
			str = "저장할 데이터가 없습니다";
		com.alert(str);
		return false;
	}

	if (com.validateGridView(SI0402G1)) {
		if (idx > 0) {
			var yesno_str = com.strModifiedCnt(SI0402G1) + "의 데이터가 저장됩니다. \n 진행하시겠습니까?";
			com.confirm(yesno_str, function(rtn) {
				if (rtn) {				
					com.saveData(SI0402G1,null,sub_SI0402G1S0);
				}
			});
		}
	}

};

//맵 클릭 이벤트
scwin.onClickMap = function(e) {
	curPoint = e;
	//scwin.disp_check_all();


	//var routNm = SI0402G0.getFocusedCellData("ROUT_NM");

	if(isSttnAdd) {
		scwin.select_sttn(true);
	}
	else if(isCrossAdd){
		scwin.select_cross(true);
	}
	else if(isVertexAdd){
		routMap.addVertexByClick("map_SI0402", SI0402G1,curRoutId,e);
	}else if(isPathAdd){
		routMap.addPathByClick("map_SI0402", SI0402G1,curRoutId,e);
	}
}

scwin.nodeChangeCb = function(idx) {
	if(isPathAdd || isVertexAdd || isSttnAdd || isCrossAdd) return;

	SI0402G1.setFocusedCell(idx,"NODE_ID");

	var nodeType = dlt_BMS_ROUT_NODE_CMPSTN.getCellData(idx,"NODE_TYPE");

	if(nodeType == "NT002"){
		scwin.select_sttn(false);
	}
	else if(nodeType == "NT001"){
		scwin.select_cross(false);
	}
}


scwin.btn_del_onclick = function() {

};

scwin.btn_sub_del = function() {

	com.delGrid(SI0402G1);
};

scwin.btn_sub_move = function() {
	if(com.isEmpty(dma_sub_search.get("CONTENT"))==false){
		com.gridMove(SI0402G1, "NODE_ID,NODE_NM", dma_sub_search.get("CONTENT"), SI0402G1.getFocusedRowIndex());
	}
};


