scwin.addPathClick = function(){
	if(isPathAdd) {
		isPathAdd = false;
	} else {;
		isPathAdd = true;
		isSttnAdd = false;
		isVertexAdd = false;
		isCrossAdd = false;
	}
	scwin.changeGeoBtn();
}

scwin.addVertexClick = function(){
	if(isVertexAdd) {
		isVertexAdd = false;

	} else {
		isPathAdd = false;
		isSttnAdd = false;
		isVertexAdd = true;
		isCrossAdd = false;
	}
	scwin.changeGeoBtn();
}

scwin.addSttnClick = function(){
	if(isSttnAdd) {
		isSttnAdd = false;
	} else {
		isPathAdd = false;
		isSttnAdd = true;
		isVertexAdd = false;
		isCrossAdd = false;
	}
	scwin.changeGeoBtn();
}

scwin.addCrossClick = function(){
	if(isCrossAdd) {
		isCrossAdd = false;
	} else {
		isPathAdd = false;
		isSttnAdd = false;
		isVertexAdd = false;
		isCrossAdd = true;
	}
	scwin.changeGeoBtn();
}

scwin.subGridUp = function(){
	var idx = SI0402G1.getFocusedRowIndex();
	com.getGridViewDataList(SI0402G1).rowMoveUp(idx);
}

scwin.subGridDown = function(){
	var idx = SI0402G1.getFocusedRowIndex();
	com.getGridViewDataList(SI0402G1).rowMoveDown(idx);
}

scwin.refreshGridStatus = function(){
	var routeData = dlt_BMS_ROUT_NODE_CMPSTN.getAllJSON();
	for(var i = 0; i < routeData.length; i++) {
		if(routeData[i].NODE_TYPE == "NT001" || routeData[i].NODE_TYPE == "NT002"){
			SI0402G1.setCellReadOnly(i, "NODE_TYPE", true);
		}
	}
}

scwin.disp_check_all = function(){
	disp_check.getCheckboxList()[0].checked = true;
	disp_check.getCheckboxList()[1].checked = true;
	disp_check.getCheckboxList()[2].checked = true;
	disp_check.getCheckboxList()[3].checked = true;
	routMap.setDispCheck("map_SI0402", disp_check.getValue());
}

scwin.disp_check_normal = function(){
	disp_check.getCheckboxList()[0].checked = true;
	disp_check.getCheckboxList()[2].checked = true;
	disp_check.getCheckboxList()[3].checked = true;
	routMap.setDispCheck("map_SI0402", disp_check.getValue());
}



scwin.releaseGeoBtn = function(){
	isPathAdd = false;
	isVertexAdd = false;
	isSttnAdd = false;
	isCrossAdd = false;
	scwin.changeGeoBtn();
}

scwin.changeGeoBtn = function(){

	var temp = wfm_subBtn2.getWindow().btn_sub_generator;

	if(isPathAdd) {
		temp.getChild(0,"btn_sub").setValue("경로종료");
		temp.getChild(0,"btn_sub").addClass("end");
	}
	else {
		temp.getChild(0,"btn_sub").setValue("경로추가");
		temp.getChild(0,"btn_sub").removeClass("end");
	}

	if(isVertexAdd) {
		temp.getChild(1,"btn_sub").setValue("버텍스종료");
		temp.getChild(1,"btn_sub").addClass("end");
	}
	else {
		temp.getChild(1,"btn_sub").setValue("버텍스추가");
		temp.getChild(1,"btn_sub").removeClass("end");
	}

	if(isSttnAdd) {
		temp.getChild(2,"btn_sub").setValue("정류소종료");
		temp.getChild(2,"btn_sub").addClass("end");
	}
	else {
		temp.getChild(2,"btn_sub").setValue("정류소추가");
		temp.getChild(2,"btn_sub").removeClass("end");
	}

	if(isCrossAdd) {
		temp.getChild(3,"btn_sub").setValue("교차로종료");
		temp.getChild(3,"btn_sub").addClass("end");
	}
	else {
		temp.getChild(3,"btn_sub").setValue("교차로추가");
		temp.getChild(3,"btn_sub").removeClass("end");
	}

	if(!isPathAdd&&!isVertexAdd&&!isSttnAdd&&!isCrossAdd) {
		routMap.setMapCursor("map_SI0402", "pointer");
	}
	else {
		scwin.disp_check_all();
		routMap.setMapCursor("map_SI0402", "crosshair");
	}
}
