		for (i=0; i<dlt_BMS_DVC_INFO.getDataLength(); i++) {
			//비정상
			if (dlt_BMS_DVC_INFO.getCellData(i, "DVC_COND") == "DC021") {
				VD0202G1.setCellClass(i, "DVC_COND", "cond_abnormal");
			}
			//정상
			else if (dlt_BMS_DVC_INFO.getCellData(i, "DVC_COND") == "DC020") {
				VD0202G1.setCellClass(i, "DVC_COND", "cond_normal");
			}
		}

		var topCoords = null;
		var leftCoords = null;
		$("#mf_tac_layout_contents_"+ param.menuCode+ "_body_busImage").empty();

		for(var i=0; i<dlt_BMS_DVC_INFO.getDataLength(); i++) {
			var imgSrc = null;
