	scwin.VD0201G1_oncellclick = function(row,col,colId) {
		var dvc_ctrIndex = VD0202G1.getColumnIndex("DVC_CTR");
		var dvc_kind = dlt_BMS_DVC_INFO.getCellData(row, "DVC_KIND");
		var mngId = dlt_BMS_DVC_INFO.getCellData(row, "MNG_ID");
		var param = com.getParameter();
		var buttonParentId = "#mf_tac_layout_contents_" + param.menuCode + "_body_VD0202G1_cell_" + row+ "_" + dvc_ctrIndex;
		var dataDisabled = $(buttonParentId).hasClass("hideData");	


		var gridColumn = VD0202G1.getColumnID(col);

		if (gridColumn == "DVC_CTR" && dataDisabled == false && dvc_kind == "DK001") {

			scwin.openMenu("OBE 제어관리","/ui/BMS/VD0301/VD0301M01.xml", "MN00050301", dlt_BMS_DVC_INFO.getCellData(row, "DVC_ID"));				
		}		
	};

	function imgOnclick(id) {
		var rowIndex = dlt_BMS_DVC_INFO.getMatchedIndex("DVC_ID", id);
		VD0202G1.setFocusedCell(rowIndex, "DVC_ID", false);
	}

