	function connect() {
		var socket = new SockJS('/websocket');
		stompClient = Stomp.over(socket);

		stompClient.connect({}, onConnected, onError);

	}	


	function onConnected(frame) {
		//setConnected(true);

		console.log('Connected: ' + frame);

	    // Subscribe to the Public Topic

	    stompClient.subscribe('/topic/public', scwin.onMessageReceived);

	}


	function onError(error) {
		showMessage('Could not connect to WebSocket server. ' + error);
	}	

	scwin.onMessageReceived = function(payload) {
		var jsonObj = JSON.parse(payload.body);

		var attrId = jsonObj.ATTR_ID;
		//var attrId = jsonObj.getData("ATTR_ID");

		if(attrId == 1000){
			try{
				var curIndexArr = dlt_BRT_CUR_OPER_INFO.getMatchedIndex("VHC_ID", jsonObj.VHC_ID);

				dlt_BRT_CUR_OPER_INFO.setRowJSON(curIndexArr[0], jsonObj, true);

				jsonObj = dlt_BRT_CUR_OPER_INFO.getDataRow(curIndexArr[0]);

				var rowData = dlt_BRT_CUR_OPER_INFO.getCellData(VD0202G0.getFocusedRowIndex(), "VHC_ID")

				setTimeout(function() {
					routMap.showVehicle2("map_VD0202", jsonObj, rowData, VD0202G0, curIndexArr[0], MO0101G0.getFocusedRowIndex());	
				},100);			
			} catch (e) {
				console.log("[scwin.onMessageReceived] Exception :: " + e.message);
			}

		}else if(attrId == 2000){
			var index = dlt_BMS_DVC_INFO.getMatchedIndex("DVC_ID", jsonObj.DVC_ID);

			dlt_BMS_DVC_INFO.setRowJSON(index, jsonObj, true);

		}

		//showMessage(jsonObj);
	}

	function showMessage(message) {
		$("#mf_tac_layout_contents_MN00050202_body_message").append("<tr><td>" + message + "</td></tr>");
	}	



	scwin.VD0202G0_onrowindexchange = function(row,oldRow) {
		var focusOption = {keyMap : dma_sub_search};
		com.changeDualGrid(VD0202G0, VD0202G1, null, sub_VD0202G1R0, focusOption, row, oldRow);	
		var routID = dlt_BRT_CUR_OPER_INFO.getCellData(VD0202G0.getFocusedRowIndex(), "ROUT_ID");
		dma_sub_search.set("ROUT_ID", routID);
		com.executeSubmission(sub_VD0202G2R0);

		var param = com.getParameter();
		var busClass = null;
		var vhcKind = dlt_BRT_CUR_OPER_INFO.getCellData(row, "VHC_KIND");
		if (vhcKind == "VK001") {
			busClass = "brt_bus_img";	
		} else if (vhcKind == "VK002") {
			busClass = "woojin_bus_img";	
		} else if (vhcKind == "VK003") {
			busClass = "normal_bus_img";	
		} else {
			busClass = "normal_bus_img";	
		}
		var id = "#mf_tac_layout_contents_"+ param.menuCode+ "_body_busImage";
		$("#mf_tac_layout_contents_"+ param.menuCode+ "_body_busImage").attr("class", busClass);				 
//		routMap.initMapInfo("map_VD0202"); 

	};

	scwin.sub_VD0202G0R0_submitdone = function(e) {
		//com.setFocusedCell(VD0202G0, "REP_ROUT_NM");
		if(dlt_BRT_CUR_OPER_INFO.getAllJSON().length > 0) {
			VD0202G0.setFocusedCell(0, "REP_ROUT_NM");
		}
		else {
			dma_sub_search.set("ROUT_ID", null);
			dma_sub_search.set("VHC_ID", null);
			//$p.executeSubmission(sub_VD0202G1R0);
			$p.executeSubmission(sub_VD0202G2R0);				
		}

		$(function () {
