			$( "#disconnect" ).click(function() { disconnect(); });

			$("form").on('submit', function (e) {
				e.preventDefault();
			});
			$( "#send" ).click(function() { sendMessage(); });

			connect();

		});			

//		var list = com.getGridDispJsonData(
