	scwin.VD0202G1_onrowindexchange = function(row,oldRow) {
		var param = com.getParameter();

		var topCoords = null;
		var leftCoords = null;
		$("#mf_tac_layout_contents_"+ param.menuCode+ "_body_busImage").empty();

		for(var i=0; i<dlt_BMS_DVC_INFO.getDataLength(); i++) {
			var imgSrc = null;
			var ctr_yn = dlt_BMS_DVC_INFO.getCellData(i, "CTR_YN");
			if (ctr_yn == "N") {
				VD0202G1.setDisabled("cell", i, "DVC_CTR", true);
				//제어 버튼 안보이게 처리
				VD0202G1.setCellClass(i, "DVC_CTR", "hideData");
			}

			var dvc_kind = dlt_BMS_DVC_INFO.getCellData(i, "DVC_KIND");
			switch (dvc_kind){
				case "DK001":
					imgSrc = "/cm/images/bms/OBE_normal.png";
					break;
				case "DK002":
					imgSrc = "/cm/images/bms/drvTerminal_normal.png";
					break;
				case "DK003":
					imgSrc = "/cm/images/bms/psgGuide_normal.png";
					break;
				case "DK004":
					imgSrc = "/cm/images/bms/routMap_normal.png";
					break;
				case "DK005":
					imgSrc = "/cm/images/bms/keyPad_normal.png";
					break;

				//행선지 정면
				case ("DK006" || "DK007"):
					imgSrc = "/cm/images/bms/desGuide_normal.png";
					break;
				//행선지 측면	
				case ("DK008" || "DK009" || "DK012"):
					imgSrc = "/cm/images/bms/desGuide_normal.png";
					break;
				//행선지 후면	
				case ("DK013"):
					imgSrc = "/cm/images/bms/desGuide_normal.png";
					break;
				case "DK011":
					imgSrc = "/cm/images/bms/green_circle.png";
					if(dvc_cond == "DC021")
						imgSrc = "/cm/images/bms/red_circle.png";
					break;
			}

			if (imgSrc != "" || imgSrc != null){

				var coordsData = dlt_BMS_DVC_INFO.getCellData(i, "DVC_COORDS");
				if(coordsData != null || coordsData != "") {
					var coordsSplit = coordsData.split(",");
					var topCoords = coordsSplit[0];
					var leftCoords = coordsSplit[1];
				}
				var imgId = dlt_BMS_DVC_INFO.getCellData(i, "DVC_ID");

				var str = "";
				str = "<div id='"+ imgId+ "' style='position:absolute; top:"+topCoords+ "px; left:"+leftCoords+"px;' class='w2group' onclick='imgOnclick(this.id);'>"
				+ "<img xmlns='http://www.w3.org/1999/xhtml' src='"+ imgSrc+ "'>"
				+ "</div>";
				$("#mf_tac_layout_contents_"+ param.menuCode+ "_body_busImage").append(str);				
			} // end if

		} // end for

	  var selectedDvcKind = dlt_BMS_DVC_INFO.getCellData(row, "DVC_KIND");
	  switch (selectedDvcKind){
			case "DK001":
		//		selectImg = "/cm/images/bms/OBE_normal.png";
				selectImg = "/cm/images/bms/bad_icon.PNG";
				break;
			case "DK002":
		//		selectImg = "/cm/images/bms/drvTerminal_normal.png";
				selectImg = "/cm/images/bms/bad_icon.PNG";
				break;
			case "DK003":
		//		selectImg = "/cm/images/bms/psgGuide_normal.png";
				selectImg = "/cm/images/bms/bad_icon.PNG";
				break;
			case "DK004":
		//		selectImg = "/cm/images/bms/routMap_normal.png";
				selectImg = "/cm/images/bms/bad_icon.PNG";
				break;
			case "DK005":
		//		selectImg = "/cm/images/bms/keyPad_normal.png";
				selectImg = "/cm/images/bms/bad_icon.PNG";
				break;

			//행선지 정면
			case ("DK006" || "DK007"):
		//		selectImg = "/cm/images/bms/desGuide_normal.png";
				selectImg = "/cm/images/bms/bad_icon.PNG";
				break;
			//행선지 측면	
			case ("DK008" || "DK009" || "DK012"):
		//		selectImg = "/cm/images/bms/desGuide_normal.png";
				selectImg = "/cm/images/bms/bad_icon.PNG";
				break;
			//행선지 후면	
			case ("DK013"):
		//		selectImg = "/cm/images/bms/desGuide_normal.png";
				selectImg = "/cm/images/bms/bad_icon.PNG";
				break;
			case "DK011":
		//		selectImg = "/cm/images/bms/green_cicle_normal.png";
				selectImg = "/cm/images/bms/bad_icon.PNG";
				break;		
		}      

      //이미지 변경
      var dvcId = dlt_BMS_DVC_INFO.getCellData(row, "DVC_ID");
      $('#'+dvcId).children('img').attr("src", selectImg);		
	};

	scwin.sub_VD0202G2R0_submitdone = function(e) {
