	scwin.btn_save_onclick = function(){
		var idx = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getModifiedIndex().length;
		if(idx>0){
			var yesno_str = "운행계획 상세 데이터가 저장됩니다. \n 진행하시겠습니까?";

			com.confirm(yesno_str, function(rtn) {
				if (rtn) {
					com.executeSubmission(sub_AL0203G1S0);
				}
			});
		}
	};

	//베포버튼
	scwin.btn_distri_onclick = function(){

		var jsondata = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getAllJSON();
		var dayDiv = dlt_BMS_ROUT_MST.getCellData(AL0203G0.getFocusedRowIndex(), "DAY_DIV");
		var repRoutId = dlt_BMS_ROUT_MST.getCellData(AL0203G0.getFocusedRowIndex(), "REP_ROUT_ID");
		var repRoutNm = dlt_BMS_ROUT_MST.getCellData(AL0203G0.getFocusedRowIndex(), "REP_ROUT_NM");

		if(dayDiv == 'DY001'){
			var day = '평일';		
		}else{
			var day = '휴일';
		}

		var data = {data : jsondata, DAYDIV : dayDiv, REPROUTID : repRoutId, callbackFn : "scwin.AL0203P0_callbackFn"};
		if(jsondata.length > 0){
			var options = { id : "AL0203P01",
							popupName : repRoutNm + day + " 배포 날짜",
							width : "450px",
							height : "330px",
							modal : true};
			com.openPopup("/ui/BRT/AL0203/AL0203P01.xml", options, data);
		}
	}

	scwin.setGridViewInfo = function() {

		var gridHeader ="", gridBody="";
		var rowData = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getAllJSON();
		var rowCntArr = dlt_BRT_OPER_ALLOC_PL_NODE_CNT.getAllJSON();

		var maxCnt = 0;

		for(var i = 0; i < rowCntArr.length; i++) {
			if(maxCnt < rowCntArr[i].CNT)
				maxCnt = rowCntArr[i].CNT;
		}

		if(maxCnt==0)maxCnt=30;

		if(rowData.length == 0 ) {
			dma_sub_search.set("NODE_TYPE", "NT002"); 
			com.executeSubmission(sub_MAIN_ROUT_NODE_CPMSTN);
			return;
		}

		dlt_VIEW_OPER_PL_NODE_INFO.removeAll();

		gridHeader +='<w2:row style="" id="first_row">';
		gridHeader +='<w2:column blockSelect="false" readOnly="true" id="chkHeader" style="height:20px" inputType="checkbox" width="42" value="선택" rowSpan="2" sortable="false"></w2:column>';
		gridHeader +='<w2:column blockSelect="false" id="'+'h'+'OPER_SN'+'" style="height:20px" inputType="text" width="70" value="'+'운행회차'+'" rowSpan="2"></w2:column>';
		for(var i = 0; i < maxCnt; i++) {
			var nodeNm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(i, "NODE_NM");
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'NODE_NM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="140" colSpan="2" value="'+nodeNm+'"></w2:column>';
		}
		gridHeader +='</w2:row>';

		gridBody += '<w2:column blockSelect="false" disabled="false" falseValue="0" defaultValue="" trueValue="1" id="chk" readOnly="false" style="height:20px" inputType="checkbox" width="70"></w2:column>';
		gridBody += '<w2:column blockSelect="false" id="'+'OPER_SN'+'" style="height:20px" inputType="text" width="70"></w2:column>';

		gridHeader +='<w2:row style="" id="second_row">';
		//maxCnt만큼 body 생성  
		for(var i = 0; i < maxCnt; i++) {
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'ARRV_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="도착시간"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'DPRT_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="출발시간"></w2:column>';
			gridBody += '<w2:column blockSelect="false" id="'+'ARRV_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'DPRT_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
		}
		gridHeader +='</w2:row>';

		var rowIndex = 0;
		var rowCnt = 0;

		for(var i = 0; i < rowCntArr.length; i++) {
			var j = 0;
			rowIndex += rowCnt;
			rowCnt =  rowCntArr[i].CNT;
			var columnVal = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(rowIndex+j, "OPER_SN");
			if(com.isEmpty(columnVal))break;
			var idx = dlt_VIEW_OPER_PL_NODE_INFO.insertRow();
			dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "OPER_SN", columnVal);

			for(j = 0; j < rowCnt; j++) {
				var nodeStTm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(rowIndex+j, "DPRT_TM");
				var nodeEdTm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(rowIndex+j, "ARRV_TM");
				var nodeSn = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(rowIndex+j, "NODE_SN");

				dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "ARRV_TM_"+j, nodeEdTm);
				dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, 'DPRT_TM_'+j, nodeStTm);
				dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, 'NODE_SN_'+j, nodeSn);
			}
		}

		//gridView 정보 정의 
		//xml 형식을 정의 하면 <w2:gridView 에 xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" 포함 합니다.
		var gridStr = '<w2:gridView xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" dataList="dlt_VIEW_OPER_PL_NODE_INFO" ev:onafteredit="scwin.AL0203G1_onafteredit" '
						+ 'scrollByColumn="true" ignoreCellClick="false"  fixedColumnWithHidden="true" id="AL0203G1" ev:onrowindexchange="scwin.AL0203G1_onrowindexchange" class ="cm_grid right" useShiftKey="false" '
						+ ' ev:oncellclick="scwin.AL0203G1_oncellclick" mergeCells="byrestriccol" focusMode="row" mergeCol="OPER_SN, WAY_DIV" >'
		+ '<w2:header id="header1" style="text-align:left">'
		+ gridHeader
		+'</w2:header>'
		+'<w2:gBody id="gBody1" style="">'
		+'<w2:row id="row2" style="">'
		+ gridBody
		+'</w2:row>'
		+'</w2:gBody>'
		+'</w2:gridView>' ;

		AL0203G1.setGridStyle(WebSquare.xml.parse( gridStr , true ));
		autoOptions.Sub1.grid = AL0203G1;
		AL0203G1.setFixedColumn(1);


		dlt_VIEW_OPER_PL_NODE_INFO.modifyAllStatus("R")
	};

