	scwin.openPopUpChart = function(){


		var jsonData = AL0203G1.getCheckedJSON("chk");
		var chkCnt = jsonData.length;

		var transData = new Array();
		for(var i=0; i<chkCnt; i++){
			for(var j=0; j<dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getRowCount(); j++){
				if(jsonData[i].OPER_SN  ==  dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(j, "OPER_SN")){
					transData.push(dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getRowJSON(j));
				}
			}
		}

		//var operSn = dlt_VIEW_OPER_PL_NODE_INFO.getCellData(AL0203G1.getCheckedIndex("chk"), "OPER_SN");
		var routId = dlt_BMS_ROUT_MST.getCellData(AL0203G0.getFocusedRowIndex(), "ROUT_ID");
		var dayDiv = dlt_BMS_ROUT_MST.getCellData(AL0203G0.getFocusedRowIndex(), "DAY_DIV");
		var routNm = dlt_BMS_ROUT_MST.getCellData(AL0203G0.getFocusedRowIndex(), "ROUT_NM");
		//var operSn = dlt_VIEW_OPER_PL_NODE_INFO.getCellData(AL0203G1.getFocusedRowIndex(), "OPER_SN");

		 var data = { data : routId, data2 : dayDiv, data3 : routNm, data4:transData, data5:chkCnt, callbackFn : "" };
         var options = { id : "AL0203P0",
         				 width : "1600px",
         				 height : "800px",
                         popupName : "운행 계획 시공도", 
                         modal : true};

		com.openPopup("/cm/common/popUpChart.xml", options, data);
	};

	scwin.sub_AL0203G0R0_submitdone = function(e) {
