scwin.onpageload = function() {
	scwin.param = com.getParameter();
	scwin.closeCallbackFncName = scwin.param.callbackFn;

	dma_searchItem.set("DAY_DIV", scwin.param.DAYDIV);
	dma_searchItem.set("REP_ROUT_ID", scwin.param.REPROUTID);

	com.executeSubmission(sub_AL0203P0R0);
};

scwin.onpageunload = function() {

};

scwin.btn_confirm_onclick = function() {

	//var cnt = scwin.param.data.length;
	var cnt = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getRowCount();
	var dayDiv = scwin.param.DAYDIV;
	var startDateValue = AL0302C0.getValue();
	var lastDateValue = AL0302C1.getValue();

	var week = ['일', '월', '화', '수', '목', '금', '토']; 

	var startDate = startDateValue.slice(0,4) + '-' + startDateValue.slice(4, 6) + '-' + startDateValue.slice(6,8)
	var lastDate = lastDateValue.slice(0,4) + '-' + lastDateValue.slice(4, 6) + '-' + lastDateValue.slice(6,8)

	var curDate = new Date(startDate);
	var j = 0;

	var relDt = [];
	while(curDate <= new Date(lastDate)) {
		var dayOfWeek = week[new Date(curDate).getDay()];

		if(dayDiv == 'DY001'){
			if(dayOfWeek != '토' && dayOfWeek != '일'){
				//dlt_OPER_DT.insertRow();
				//dlt_OPER_DT.setCellData(j, "OPER_DT", curDate.toISOString().split("T")[0]);
				relDt.push(curDate.toISOString().split("T")[0]);
				curDate.setDate(curDate.getDate() + 1);
				j++;
			}else{
				curDate.setDate(curDate.getDate() + 1);
			}
		}else if(dayDiv == 'DY002'){
			if(dayOfWeek == '토' || dayOfWeek == '일'){
				//dlt_OPER_DT.insertRow();
				//dlt_OPER_DT.setCellData(j, "OPER_DT", curDate.toISOString().split("T")[0]);
				relDt.push(curDate.toISOString().split("T")[0]);
				curDate.setDate(curDate.getDate() + 1);
				j++;
			}else{
				curDate.setDate(curDate.getDate() + 1);
			}
		}
	}

	dma_relDt.set("relDt", relDt);

	if(dma_relDt.get("relDt").length != 0){
		com.confirm("배포하시겠습니까?", function(rtn) {
		 	if (rtn) { 
		 		//배포 동의.
		 		com.executeSubmission(sub_AL0203P0S0);
		 	}
		});
	}else{
		com.alert("배포일을 선택해주세요.");
	}

