			var options = {
				fileName : str+".xls", //[defalut: excel.xlsx] 다운로드하려는 파일의 이름으로 필수 입력 값 (지원하는 타입 => xls, xlsx, csv)
				fileType : "xls",
				useFooter : true
			};
			var infoArr = {};
			com.gridDataDownLoad(AL0205G1, options, infoArr);

		}else if(nodeType == "NT002"){
			var str = dlt_BMS_STTN_MST.getCellData(AL0205G0.getFocusedRowIndex(), "STTN_NM") + " - 정류소 상세 시간표";
			var options = {
