	//정류소,교차로 상세 시간표 동적 그리드함수
	scwin.setGridViewInfo = function() {
		var gridHeader ="", gridBody="";
		var rowData = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getAllJSON();
		var rowCntArr = dlt_BRT_OPER_ALLOC_PL_NODE_CNT.getAllJSON();
		var repRoutNmList = [];
		var maxCnt = 0;
		var dayDivLength = 0;
		var holiDivLength = 0;

		for(var i = 0; i < rowCntArr.length; i++) {
			if(maxCnt < rowCntArr[i].CNT)
				maxCnt = rowCntArr[i].CNT;
		}

		if(maxCnt==0)maxCnt=30;

		if(rowData.length == 0 ) {
			//dma_sub_search.set("NODE_TYPE", "NT002"); 
			com.executeSubmission(sub_MAIN_ROUT_NODE_CPMSTN);
			return;
		}

		dlt_VIEW_OPER_PL_NODE_INFO.removeAll();

		//평일,휴일 노선 수 cnt
		dayDivLength = dlt_BRT_OPER_ALLOC_PL_NODE_ROUT_NM.getMatchedIndex("DAY_DIV", "DY001").length;
		holiDivLength = dlt_BRT_OPER_ALLOC_PL_NODE_ROUT_NM.getMatchedIndex("DAY_DIV", "DY002").length;

		//Header first_row
		gridHeader +='<w2:row style="" id="first_row">';
		//gridHeader +='<w2:column blockSelect="false" id="OPER_SN" style="height:20px" inputType="text" width="70" value="'+'운행순번'+'" rowSpan="3"></w2:column>';
		gridHeader +='<w2:column blockSelect="false" id="DAY" style="height:20px" inputType="text" width="70" value="'+'평일'+'" colSpan="'+dayDivLength * 3+'"></w2:column>';
		if(holiDivLength > 0){
			gridHeader +='<w2:column blockSelect="false" id="HOLI" style="height:20px" inputType="text" width="70" value="'+'휴일'+'" colSpan="'+holiDivLength * 3+'"></w2:column>';
		}
		gridHeader +='</w2:row>';

		//운행순번 Body
		//gridBody += '<w2:column blockSelect="false" id="OPER_SN" style="height:20px" inputType="text" width="70"></w2:column>';

		//Header second_row
		gridHeader +='<w2:row style="" id="second_row">';

		for(var i = 0; i < dlt_BRT_OPER_ALLOC_PL_NODE_ROUT_NM.getRowCount(); i++) { //maxCnt에서 -> dlt_BRT_OPER_ALLOC_PL_NODE_ROUT_NM.getRowCount();으로 수정
			var repRoutNm = dlt_BRT_OPER_ALLOC_PL_NODE_ROUT_NM.getCellData(i, "REP_ROUT_NM");
			repRoutNmList[i] = repRoutNm
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'REP_ROUT_NM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="140" colSpan="3" value="'+repRoutNm+'"></w2:column>';
		}
		gridHeader +='</w2:row>'

		//Header third_row
		gridHeader +='<w2:row style="" id="third_row">';
		//maxCnt만큼 body 생성
		for(var i = 0; i < dlt_BRT_OPER_ALLOC_PL_NODE_ROUT_NM.getRowCount(); i++) { //maxCnt에서 -> dlt_BRT_OPER_ALLOC_PL_NODE_ROUT_NM.getRowCount();으로 수정
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'ROUT_NM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="75" colSpan="'+1+'" value="노선명"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'ARRV_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="도착시간"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'DPRT_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="출발시간"></w2:column>';
			gridBody += '<w2:column blockSelect="false" id="'+'ROUT_NM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'ARRV_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'DPRT_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
		}
		gridHeader +='</w2:row>'

		//Body 데이터 생성
		for(var b = 0; b < repRoutNmList.length; b++){ //repRoutNmList.length == 대표노선 col 개수
			for(var i = 0; i < rowCntArr.length; i++) { //rowCntArr.length == 전체 row 개수
				var dayRow = 0;
				var holRow = 0;

				dlt_VIEW_OPER_PL_NODE_INFO.insertRow();

				for(var x = 0; x < rowData.length; x++){
					var repRoutNm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "REP_ROUT_NM");
					var dayDiv = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "DAY_DIV");
					if(repRoutNmList[b] == repRoutNm && dayDiv == "DY001" && b < dayDivLength){
						var nodeStTm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "DPRT_TM");
						var nodeEdTm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "ARRV_TM");
						var routNm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "ROUT_NM");

						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(dayRow, "ARRV_TM_"+b, nodeEdTm);
						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(dayRow, "DPRT_TM_"+b, nodeStTm);
						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(dayRow, "ROUT_NM_"+b, routNm + "(" + dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "OPER_SN") + ")");
						dlt_VIEW_OPER_PL_NODE_INFO.modifyRowStatus(dayRow, "R");
						dayRow++;
					}
				}
				for(var x = 0; x < rowData.length; x++){
					var repRoutNm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "REP_ROUT_NM");
					var dayDiv = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "DAY_DIV");
					if(repRoutNmList[b] == repRoutNm && dayDiv == "DY002"){
						var nodeStTm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "DPRT_TM");
						var nodeEdTm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "ARRV_TM");
						var routNm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "ROUT_NM");

						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(holRow, "ARRV_TM_"+dayDivLength, nodeEdTm);
						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(holRow, "DPRT_TM_"+dayDivLength, nodeStTm);
						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(holRow, "ROUT_NM_"+dayDivLength, routNm + "(" + dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(x, "OPER_SN") + ")");
						dlt_VIEW_OPER_PL_NODE_INFO.modifyRowStatus(holRow, "R");
						holRow++;
					}

				}
			}
		}

		//데이터가 없는 row 삭제
		for(var x = 0; x < dlt_VIEW_OPER_PL_NODE_INFO.getRowCount(); x++){
			var rowStat = dlt_VIEW_OPER_PL_NODE_INFO.getRowStatus(x);
			if(rowStat == "C"){
				dlt_VIEW_OPER_PL_NODE_INFO.removeRange(x, dlt_VIEW_OPER_PL_NODE_INFO.getRowCount()-1);
				break;
			}
		}


		/*for(var i = 0; i < rowCntArr.length; i++) { //rowCntArr.length == row개수
			var j = 0;
			var y = 0;
			var a = 0;
			rowIndex += rowCnt;
			rowCnt =  rowCntArr[i].CNT;
			var columnVal = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(rowIndex+j, "OPER_SN");
			if(com.isEmpty(columnVal))break;
			var idx = dlt_VIEW_OPER_PL_NODE_INFO.insertRow();
			dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "OPER_SN", columnVal);

			for(j = 0; j < repRoutNmList.length; j++) { //col개수
				var repRoutNm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(rowIndex+j, "REP_ROUT_NM");

				for(var x = 0; x < repRoutNmList.length; x++){
					if(repRoutNmList[a] == repRoutNm){
						var nodeStTm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(rowIndex+j, "DPRT_TM");
						var nodeEdTm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(rowIndex+j, "ARRV_TM");
						var routNm = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(rowIndex+j, "ROUT_NM");

						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "ARRV_TM_"+y, nodeEdTm);
						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "DPRT_TM_"+y, nodeStTm);
						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "ROUT_NM_"+y, routNm + "(" + dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getCellData(rowIndex+j, "OPER_SN") + ")");
						y++;
						a++;
						break;
					}else{
						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "ARRV_TM_"+y, "-");
						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "DPRT_TM_"+y, "-");
						dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "ROUT_NM_"+y, "-");
						y++;
						a++;
					}

				}

			}
		}*/
		var footerColSpanNum = dayDivLength * 3 + holiDivLength * 3;

		//gridView 정보 정의
		//xml 형식을 정의 하면 <w2:gridView 에 xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" 포함 합니다.
		var gridStr = '<w2:gridView xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" dataList="dlt_VIEW_OPER_PL_NODE_INFO" ev:onafteredit="scwin.AL0205G1_onafteredit" '
						+ 'scrollByColumn="true" ignoreCellClick="false"  fixedColumnWithHidden="true" id="AL0205G1" ev:onrowindexchange="scwin.AL0205G1_onrowindexchange" class ="cm_grid right" useShiftKey="false" '
						+ ' ev:oncellclick="scwin.AL0205G1_oncellclick" mergeCells="byrestriccol" focusMode="row" mergeCol="OPER_SN, WAY_DIV" autoFit="none" readOnly="true" rowNumVisible="true" rowNumHeaderValue="No.">'
		+ '<w2:header id="header1" style="text-align:left">'
		+ gridHeader
		+'</w2:header>'
		+'<w2:gBody id="gBody1" style="">'
		+'<w2:row id="row2" style="">'
		+ gridBody
		+'</w2:row>'
		+'</w2:gBody>'

		+'<w2:footer style="" id="footer2">'
		+'<w2:row style="" id="row4">'
		+'<w2:column removeBorderStyle="false" width="NaN" inputType="expression" style="height:20px;" '
		+'id="column55" value="" blockSelect="false" displayMode="label" colSpan="'+footerColSpanNum+'" rowSpan="" '
		+'expression="&quot;데이터 개수 : &quot; + COUNT(&apos;COMP_ID&apos;)" textAlign="left">'
		+'</w2:column>'
		+'</w2:row>'
		+'</w2:footer>'

		+'</w2:gridView>' ;

		AL0205G1.setGridStyle(WebSquare.xml.parse( gridStr , true ));
		autoOptions.Sub1.grid = AL0205G1;
		AL0205G1.setFixedColumn(1);

		dlt_VIEW_OPER_PL_NODE_INFO.modifyAllStatus("R");
	};


