	scwin.dispEmptyGridView = function() {

		var gridHeader ="", gridBody="";
		var rowData = dlt_BRT_MAIN_ROUT_NODE_INFO.getAllJSON();

		dlt_VIEW_OPER_PL_NODE_INFO.removeAll();

		gridHeader +='<w2:row style="" id="first_row">';
		gridHeader +='<w2:column blockSelect="false" id="'+'h'+'OPER_SN'+'" style="height:20px" inputType="text" width="70" value="'+'운행순번'+'" rowSpan="2"></w2:column>';
		for(var i = 0; i < rowData.length; i++) {
			var nodeNm = rowData[i].NODE_NM;
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'NODE_NM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="140" colSpan="2" value="'+nodeNm+'"></w2:column>';
		}
		gridHeader +='</w2:row>';

		gridBody += '<w2:column blockSelect="false" id="'+'OPER_SN'+'" style="height:20px" inputType="text" width="70"></w2:column>';

		gridHeader +='<w2:row style="" id="second_row">';
		//maxCnt만큼 body 생성  
		for(var i = 0; i < rowData.length; i++) {
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'ARRV_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="도착시간"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'DPRT_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="출발시간"></w2:column>';
			gridBody += '<w2:column blockSelect="false" id="'+'ARRV_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'DPRT_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
		}
		gridHeader +='</w2:row>';


		//gridView 정보 정의 
		//xml 형식을 정의 하면 <w2:gridView 에 xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" 포함 합니다.
		var gridStr = '<w2:gridView xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" dataList="dlt_VIEW_OPER_PL_NODE_INFO" '
						+ 'scrollByColumn="true" id="AL0205G1" ev:onrowindexchange="scwin.AL0205G1_onrowindexchange" class ="cm_grid right" fixedColumnWithHidden="true" useShiftKey="false" mergeCells="byrestriccol" mergeCol="OPER_SN, WAY_DIV" >'
		+ '<w2:header id="header1" style="text-align:left">'
		+ gridHeader
		+'</w2:header>'
		+'<w2:gBody id="gBody1" style="">'
		+'<w2:row id="row2" style="">'
		+ gridBody
		+'</w2:row>'
		+'</w2:gBody>'
		+'</w2:gridView>' ;

		AL0205G1.setGridStyle(WebSquare.xml.parse( gridStr , true ));
		autoOptions.Sub1.grid = AL0205G1;
		AL0205G1.setFixedColumn(1);
	};

	scwin.sub_MAIN_ROUT_NODE_CPMSTN_submitdone = function(e) {
		scwin.dispEmptyGridView();
	};

	scwin.getIndexAllocPlNode = function(columnId){
		var colViewIndex = Number(columnId.substring(columnId.lastIndexOf("_")+1));
		var node_sn = dlt_VIEW_OPER_PL_NODE_INFO.getCellData(AL0205G1.getFocusedRowIndex(), "NODE_SN_"+colViewIndex);
		var open_sn = dlt_VIEW_OPER_PL_NODE_INFO.getCellData(AL0205G1.getFocusedRowIndex(), "OPER_SN");
		var columns = "OPER_SN,NODE_SN";
		var indexs = dlt_BRT_OPER_ALLOC_PL_NODE_INFO.getMatchedIndex("OPER_SN",open_sn);
		indexs = com.recurMatchedIndex(dlt_BRT_OPER_ALLOC_PL_NODE_INFO, indexs, "NODE_SN", node_sn);
		if(indexs.length>0)return indexs[0];
		else return -1;
	}

	scwin.AL0205G1_onafteredit = function(row,col,value) {
		var rowCnt = dlt_VIEW_OPER_PL_NODE_INFO.getRowCount();
		var columnId =AL0205G1.getColumnID(col);

		if (columnId.indexOf("DPRT_TM")>=0) {
			dlt_BRT_OPER_ALLOC_PL_NODE_INFO.setCellData(scwin.getIndexAllocPlNode(columnId), "DPRT_TM", value);
		}
		else if (columnId.indexOf("ARRV_TM")>=0) {
			dlt_BRT_OPER_ALLOC_PL_NODE_INFO.setCellData(scwin.getIndexAllocPlNode(columnId), "ARRV_TM", value);
		}
	};

	scwin.AL0205G1_oncellclick = function(row,col) {
		if(col==0){
			dlt_VIEW_OPER_PL_NODE_INFO.modifyRowStatus(row, 'R');
		}
	};

