	scwin.sbx_ChoiceBox_onviewchange = function(info) {
		if (info.newValue == "ALL") {
			autoChoiceBox.hide();
			search_input.setValue("");
			search_input.show("");
		} else if (info.newValue == "STTN_ID") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_sttnItem", "STTN_ID", "STTN_ID");
			autoChoiceBox.show("");
		} else if (info.newValue == "STTN_NM") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_sttnItem", "STTN_NM", "STTN_NM");
			autoChoiceBox.show("");
		} else if (info.newValue == "STTN_NO") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_sttnItem", "STTN_NO", "STTN_NO");	
			autoChoiceBox.show("");
		} else if (info.newValue == "CRS_ID") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_crsItem", "CRS_ID", "CRS_ID");
			autoChoiceBox.show("");
		} else if (info.newValue == "CRS_NM") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_crsItem", "CRS_NM", "CRS_NM");
			autoChoiceBox.show("");
		}
	};
