	TYPE1.setValue("RR00000002");

	com.executeSubmission(sub_AL0206G0R0);
};

scwin.btn_search_onclick = function(){
	com.executeSubmission(sub_AL0206G0R0);
}

scwin.sub_AL0206G0R0_submitdone = function(e) {
	com.executeSubmission(sub_selectCorCnt);
	//com.setFocusedCell(AL0206G0, "DAY_DIV");
};

scwin.sub_selectCorCnt_submitdone = function(e) {
	scwin.drawHighChart();
};

scwin.drawHighChart = function(){
	map = Highcharts.map

	var rowData = dlt_OPER_ALLOC_PL_ROUT_INFO.getAllJSON();
	var rowCntArr = dlt_OPER_ALLOC_PL_ROUT_CNT.getAllJSON();
	var rowIndex = 0;
	var rowCnt = 0;
	var cars = new Array();
	var carsRota = new Array();
	var curDate = util.getCurrentDate2();
	var year = curDate.substr(0, 4);
	var month = curDate.substr(4, 2);
	var day = curDate.substr(6, 2);

	//데이터 생성
	for(var x = 0; x < rowCntArr.length; x++){ //총 row
		var deals = new Array();
		var dealsRota = new Array();
		var rotaCnt = 1;
		var oldRotaCnt = true;
		rowIndex += rowCnt
		rowCnt = rowCntArr[x].CNT 
		for(var y = 0; y < rowCnt; y++){ //데이터 수
			var carsData = new Object();
			var dealsData = new Object();
			var carsDataRota = new Object();
			var dealsDataRota = new Object();

			carsData.ALLOC_NO = String(dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ALLOC_NO"))
			carsData.vhcNo = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "VHC_NO");
			carsData.current = 0;

			var roatTm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ROAT_TM");
			var routStTm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ROUT_ST_TM");

			var roatTmSec = util.timeToSecond(roatTm+':00');
			var routStTmSec = util.timeToSecond(routStTm+':00');

			var corId = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "COR_ID");
			var corCnt = dlt_COR_CNT.getMatchedJSON("COR_ID", corId)[0].CNT;

			if(roatTmSec <= routStTmSec && oldRotaCnt == false){
				//2교대 데이터
				carsDataRota.ALLOC_NO = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ALLOC_NO") + rowCntArr.length //+10 배차
				carsDataRota.REAL_ALLOC_NO = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ALLOC_NO");
				carsDataRota.vhcNo = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "SEC_VHC_NO");
				carsDataRota.current = 0;

				if(dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "WAY_DIV") == 'WD001'){
					dealsDataRota.rentedTo = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "WAY_ASC_NM");
				}else{
					dealsDataRota.rentedTo = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "WAY_DESC_NM");
				}

				dealsDataRota.vhcNo = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "SEC_VHC_NO");
				dealsDataRota.drvNm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "SEC_DRV_NM");
				dealsDataRota.color = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "COR_COLOR");
				dealsDataRota.routNm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ROUT_NM");
				dealsDataRota.corNm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "COR_NM");
				dealsDataRota.operSn = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "OPER_SN");
				dealsDataRota.allocNo = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ALLOC_NO");

				var stTm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ROUT_ST_TM");
				var edTm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ROUT_ED_TM");
				dealsDataRota.overlayFrom = stTm
				dealsDataRota.overlayTo = edTm

				var stHour = stTm.substr(0, 2);
				var stMin = stTm.substr(3, 2);
				var edHour = edTm.substr(0, 2);
				var edMin = edTm.substr(3, 2);

				dealsDataRota.from = Date.UTC(year, month-1, day, stHour, stMin);
				dealsDataRota.to = Date.UTC(year, month-1, day, edHour, edMin);

				dealsRota.push(dealsDataRota);
			}else{
				//1교대 데이터
				if(rotaCnt == corCnt){
					oldRotaCnt = false;
					rotaCnt = 1;
				}else{
					oldRotaCnt = true;
					rotaCnt += 1;
				}

				if(dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "WAY_DIV") == 'WD001'){
					dealsData.rentedTo = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "WAY_ASC_NM");
				}else{
					dealsData.rentedTo = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "WAY_DESC_NM");
				}

				dealsData.vhcNo = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "VHC_NO");
				dealsData.drvNm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "DRV_NM");
				dealsData.color = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "COR_COLOR");
				dealsData.routNm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ROUT_NM");
				dealsData.corNm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "COR_NM");
				dealsData.operSn = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "OPER_SN");
				dealsData.allocNo = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ALLOC_NO");

				var stTm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ROUT_ST_TM");
				var edTm = dlt_OPER_ALLOC_PL_ROUT_INFO.getCellData(rowIndex+y, "ROUT_ED_TM");
				dealsData.overlayFrom = stTm
				dealsData.overlayTo = edTm

				var stHour = stTm.substr(0, 2);
				var stMin = stTm.substr(3, 2);
				var edHour = edTm.substr(0, 2);
				var edMin = edTm.substr(3, 2);

				dealsData.from = Date.UTC(year, month-1, day, stHour, stMin);
				dealsData.to = Date.UTC(year, month-1, day, edHour, edMin);

				deals.push(dealsData);
			}
		}

		//1교대
		carsData.deals = deals
		cars.push(carsData);

		//2교대
		carsDataRota.deals = dealsRota
		carsRota.push(carsDataRota);

	};

	var newCars = cars.concat(carsRota)

	// Parse newCars data into series.
	series = newCars.map(function(car, i) {
	  var data = car.deals.map(function(deal) {
	    return {
	      id: 'deal-' + i,
	      vhcNo: deal.vhcNo,
	      drvNm: deal.drvNm,
	      rentedTo: deal.rentedTo,
		  start: deal.from,
		  end: deal.to,
		  color:deal.color,
		  routNm: deal.routNm,
		  corNm: deal.corNm,
		  overlayFrom : deal.overlayFrom,
		  overlayTo : deal.overlayTo,
		  operSn : deal.operSn,
		  allocNo : deal.allocNo,
	      y: i,
	     dataLabels: {
                enabled: true,
                format: '{point.routNm}',
                style: {
                    cursor: 'default',
                    pointerEvents: 'none',
                	fontSize:11,
                	color: 'white', //폰트 컬러
                	//textShadow : '1px 0px 0px black',
                	fontFamily : 'Malgun Gothic',
                	textOutline : '' //글씨 테두리 컬러
                },
                allowOverlap:true,
            },
	    };
	  });
	  return {
	    //name: "배차 : " + car.ALLOC_NO,
	    alloc: "B"+car.ALLOC_NO,
	    data: data,
	    current: car.deals[car.current],
	  };
	});

	//차트 그리는 부분
	var param = com.getParameter();
	var ctx = document.getElementById('mf_tac_layout_contents_'+param.menuCode+'_body_high_chart');
	var highChart = new Highcharts.ganttChart(ctx, {
	  series: series,
	  chart: {
	  	width:2500,
	    height: 650
	  },
	  title: {
	    text: TYPE1.getText()+' 배차시각화('+TYPE2.getText()+')'
	  },
	  tooltip: {
	    pointFormat: '배차번호 : {point.allocNo}<b></b><br>운행순번 : {point.operSn}<b></b><br>노선명 : {point.routNm}<b></b><br>코스명 : {point.corNm}<b></b><br>시작(시분) : {point.overlayFrom}<b></b><br>종료(시분) : {point.overlayTo}' //오버레이
	  },
	  plotOptions: {
        series: {
            pointWidth: 20 //막대 높이
        }
      },
	  xAxis: [
	  	  {
	      grid: {
	        cellHeight: 30
	      },
	      labels:{
	    	align:'center' // x축 label align
	    	},
	      currentDateIndicator: false,
	      dateTimeLabelFormats: {
	        hour: {
	          list: ['%H'+'시']
	        }
	      },
	      min: Date.UTC(year, month-1, day, 05, 00), 
	      max: Date.UTC(year, month-1, day, 26, 00)
	    },
	    {
	      grid: {
	        cellHeight: 30
	      },
	    },

	  ],
	  yAxis: {
	    type: 'category',
	    grid: {
	      //cellHeight:100,
	      borderWidth: 1,
	      columns: [{
	        title: {
	          text: '배차'
	        },
	        categories: map(series, function(s) {
	          return s.alloc;
	        })
	      }, {
	        title: {
	          text: '방향구분'
	        },
	        categories: map(series, function(s) {
	          return s.current.rentedTo;
	        })
	      }, {
	        title: {
	          text: '차량번호'
	        },
	        categories: map(series, function(s) {
	          return s.current.vhcNo;
	        })
	      }, {
	        title: {
	          text: '운전자명'
	        },
	        categories: map(series, function(s) {
	          return s.current.drvNm;
	        })
	      }]
	    }
	  }
	});
}

