head.script:
	var autoOptions = {};

	scwin.onpageload = function() {
		autoOptions = {
			Main : { grid : AL0600G0, srchSbm : sub_AL0600G0R0,
					 srchGrp : pgm_header , keyColumn : "DAY_DIV,ROUT_ID", focusColumn : "ROUT_ID", name : "운행계획 목록"},
			Sub1 : { grid : AL0600G1, srchSbm : sub_OPER_ALL_REAL_NODE, keyColumn : "OPER_SN", name : "운행계획 상세" }	
		 };

		 var mainOptions = {
	       	  EXL:scwin.btn_excel_onclick, 
		  SEARCH:{}
	      };

		var codeOptions = [ 
			{code : "WAY_DIV", compID : "AL0600G0:WAY_DIV" },
			{code : "DAY_DIV", compID : "AL0600G0:DAY_DIV" },
			{code : "ROUT_TYPE", compID : "AL0600G0:ROUT_TYPE" }
		];	
		com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID2, autoOptions, mainOptions, codeOptions);
		var mainSrhOptions = {
		};		
		com.setMainSrhBtn(wfm_mainSearchBtn, gcm.DISP_TYPE.DUAL_GRID2, autoOptions, mainSrhOptions, codeOptions);

		var subOptions2 = {
			SEARCH:[{cbFnc:scwin.openPopUpChart,nm:"시공도 보기"}
			]
		};

		com.setSubBtn2(wfm_subBtn2, autoOptions, subOptions2);



		var date = new Date();
		var year = date.getFullYear().toString();
		var month = (date.getMonth()+1).toString();
		var day = date.getDate().toString();
		var zero = 0;

		if(day.length == 1){
			day = zero + day;
		}

		if(month.length == 1){
			month = zero + month;
		}

		var today = year+month+day;

		DAYSEARCH.setValue(today);
		dma_sub_search.set("OPER_DT", today);

		com.setCommonDtl(codeOptions, function() {
			com.searchGrid(AL0600G0, sub_AL0600G0R0);
		});	

		AL0600G0.mergeCells("byrestriccol");

		com.executeSubmission(sub_AL0600SHI0);

	};

	scwin.onpageunload = function() {

	};

	//엑셀 다운로드
	scwin.btn_excel_onclick = function() {
		var routNm = dlt_BMS_ROUT_MST.getCellData(AL0600G0.getFocusedRowIndex(), "ROUT_NM");
		var dayDiv = dlt_BMS_ROUT_MST.getCellData(AL0600G0.getFocusedRowIndex(), "DAY_DIV");
		if(dayDiv == 'DY001'){
			dayDiv = '평일'
		}else if(dayDiv == 'DY002'){
			dayDiv = '휴일'
		}

		var str = routNm + dayDiv + " - 운행계획비교";
		var options = {
			fileName : str+".xls", //[defalut: excel.xlsx] 다운로드하려는 파일의 이름으로 필수 입력 값 (지원하는 타입 => xls, xlsx, csv)
			fileType : "xls",
			useFooter : true
		};
		var infoArr = {};
		com.gridDataDownLoad(AL0600G1, options, infoArr);

	};

	scwin.setGridViewInfo = function() {

		var gridHeader ="", gridBody="";
		var rowData = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getAllJSON();
		var rowCntArr = dlt_BRT_OPER_ALLOC_PL_NODE_CNT.getAllJSON();

		var maxCnt = 0;

		for(var i = 0; i < rowCntArr.length; i++) {
			if(maxCnt < rowCntArr[i].CNT)
				maxCnt = rowCntArr[i].CNT;
		}

		if(maxCnt==0)maxCnt=30;

		if(rowData.length == 0 ) {
			dma_sub_search.set("NODE_TYPE", "NT002"); 
			com.executeSubmission(sub_MAIN_ROUT_NODE_CPMSTN);
			return;
		}

		dlt_VIEW_OPER_PL_NODE_INFO.removeAll();

		gridHeader +='<w2:row style="" id="first_row">';
		gridHeader +='<w2:column blockSelect="false" id="'+'h'+'OPER_SN'+'" style="height:20px" inputType="text" width="70" value="'+'운행순번'+'" rowSpan="3"></w2:column>';
		gridHeader +='<w2:column blockSelect="false" id="'+'h'+'TOTAL_COMP_TM'+'" style="height:20px" inputType="text" width="75" value="'+'총 비교시간'+'" rowSpan="3"></w2:column>';
		for(var i = 0; i < maxCnt; i++) {
			var nodeNm = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getCellData(i, "NODE_NM");
			var nodeType = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getCellData(i, "NODE_TYPE");
			if(nodeType == 'NT001'){
				gridHeader +='<w2:column blockSelect="false" id="'+'h'+'NT001_'+i+'" style="height:20px; background-color:#d1e5ff;" textAlign="center" inputType="text" width="140" colSpan="5" value="'+nodeNm+'"></w2:column>';
			}else if(nodeType == 'NT002'){
				gridHeader +='<w2:column blockSelect="false" id="'+'h'+'NT002_'+i+'" style="height:20px; background-color:#ffdbdb;" textAlign="center" inputType="text" width="140" colSpan="5" value="'+nodeNm+'"></w2:column>';
			}
		}
		gridHeader +='</w2:row>';

		gridBody += '<w2:column blockSelect="false" id="'+'OPER_SN'+'" style="height:20px" inputType="text" width="70"></w2:column>';
		gridBody += '<w2:column blockSelect="false" id="'+'TOTAL_COMP_TM'+'" style="height:20px" inputType="text" width="70"></w2:column>';


		gridHeader +='<w2:row style="" id="second_row">';
		for(var i = 0; i < maxCnt; i++) {
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'ALLOC_INFO_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="140" colSpan="2" value="운행계획"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'CHG_INFO_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="140" colSpan="2" value="실제운행결과"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'H'+'COMPARE_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="75" colSpan="1" value="비교결과"></w2:column>';
		}
		gridHeader +='</w2:row>';


		gridHeader +='<w2:row style="" id="third_row">';
		//maxCnt만큼 body 생성  
		for(var i = 0; i < maxCnt; i++) {
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'ARRV_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="도착시간"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'DPRT_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="출발시간"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'REAL_ARRV_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="도착시간"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'REAL_DPRT_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="출발시간"></w2:column>';
			gridHeader +='<w2:column blockSelect="false" id="'+'h'+'COMPARE_TM_'+i+'" style="height:20px;" textAlign="center" inputType="text" width="70" colSpan="'+1+'" value="비교시간(초)"></w2:column>';
			gridBody += '<w2:column blockSelect="false" id="'+'ARRV_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'DPRT_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'REAL_ARRV_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'REAL_DPRT_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
			gridBody += '<w2:column blockSelect="false" id="'+'COMPARE_TM_'+i+'" style="height:20px" inputType="text" width=""></w2:column>'
		}
		gridHeader +='</w2:row>';

		var rowIndex = 0;
		var rowCnt = 0;

		for(var i = 0; i < rowCntArr.length; i++) {
			var j = 0;
			rowIndex += rowCnt;
			rowCnt =  rowCntArr[i].CNT;
			var columnVal = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getCellData(rowIndex+j, "OPER_SN");
			if(com.isEmpty(columnVal))break;
			var idx = dlt_VIEW_OPER_PL_NODE_INFO.insertRow();
			dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "OPER_SN", columnVal);

			for(j = 0; j < rowCnt; j++) {
				var nodeEdTm = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getCellData(rowIndex+j, "ARRV_TM");
				var nodeStTm = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getCellData(rowIndex+j, "DPRT_TM");
				dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "ARRV_TM_"+j, nodeEdTm);
				dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, 'DPRT_TM_'+j, nodeStTm);

				var realNodeEdTm = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getCellData(rowIndex+j, "REAL_ARRV_TM");
				var realNodeStTm = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getCellData(rowIndex+j, "REAL_DPRT_TM");
				dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "REAL_ARRV_TM_"+j, realNodeEdTm);
				dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "REAL_DPRT_TM_"+j, realNodeStTm);

				//비교시간 계산
				var operArrHour = parseInt(nodeEdTm.substring(0, 2)) * 60 * 60;
				var operArrMin = parseInt(nodeEdTm.substring(3, 5)) * 60;
				var operArrSec = parseInt(nodeEdTm.substring(6));
				var operArrTotal = operArrHour + operArrMin + operArrSec

				var operDprHour = parseInt(nodeStTm.substring(0, 2)) * 60 * 60;
				var operDprMin = parseInt(nodeStTm.substring(3, 5)) * 60;
				var operDprSec = parseInt(nodeStTm.substring(6));
				var operDprTotal = operDprHour + operDprMin + operDprSec

				var realArrHour = parseInt(realNodeEdTm.substring(0, 2)) * 60 * 60;
				var realArrMin = parseInt(realNodeEdTm.substring(3, 5)) * 60;
				var realArrSec = parseInt(realNodeEdTm.substring(6));
				var realArrTotal = realArrHour + realArrMin + realArrSec

				var realDprHour = parseInt(realNodeStTm.substring(0, 2)) * 60 * 60;
				var realDprMin = parseInt(realNodeStTm.substring(3, 5)) * 60;
				var realDprSec = parseInt(realNodeStTm.substring(6));
				var realDprTotal = realDprHour + realDprMin + realDprSec

				if(isNaN(operArrTotal) != true && isNaN(operDprTotal) != true && isNaN(realArrTotal) != true && isNaN(realDprTotal) != true){
					var compTm = (operDprTotal - operArrTotal) - (realDprTotal - realArrTotal);
					dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "COMPARE_TM_"+j, compTm + "초");
				}else{
					dlt_VIEW_OPER_PL_NODE_INFO.setCellData(idx, "COMPARE_TM_"+j, "-");
				}
			}
		}

		//gridView 정보 정의 
		//xml 형식을 정의 하면 <w2:gridView 에 xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" 포함 합니다.
		var gridStr = '<w2:gridView xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" dataList="dlt_VIEW_OPER_PL_NODE_INFO" focusMode="row" '
						+ 'scrollByColumn="true" id="AL0600G1" ev:onrowindexchange="scwin.AL0600G1_onrowindexchange" class ="cm_grid right AL0600G0" fixedColumnWithHidden="true" useShiftKey="false" mergeCells="byrestriccol" readOnly="true" mergeCol="OPER_SN, WAY_DIV" >'
		+ '<w2:header id="header1" style="text-align:left">'
		+ gridHeader
		+'</w2:header>'
		+'<w2:gBody id="gBody1" style="">'
		+'<w2:row id="row2" style="">'
		+ gridBody
		+'</w2:row>'
		+'</w2:gBody>'

		+'<w2:footer style="" id="footer2">'
		+'<w2:row style="" id="row4">'
		+'<w2:column removeBorderStyle="false" width="NaN" inputType="expression" style="height:20px;" '
		+'id="column55" value="" blockSelect="false" displayMode="label" colSpan="330" rowSpan="" '
		+'expression="&quot;데이터 개수 : &quot; + COUNT(&apos;COMP_ID&apos;)" textAlign="left">'
		+'</w2:column>'
		+'</w2:row>'
		+'</w2:footer>'

		+'</w2:gridView>' ;

		AL0600G1.setGridStyle(WebSquare.xml.parse( gridStr , true ));
		autoOptions.Sub1.grid = AL0600G1;
		AL0600G1.setFixedColumn(2);

		//비교결과 색변경, 총 비교시간 계산
		rowIndex = 0;
		rowCnt = 0;
		for(var i = 0; i < rowCntArr.length; i++) { //rowCntArr.length == 총 row 수
			var totalCompTm = 0;
			rowIndex += rowCnt;
			rowCnt =  rowCntArr[i].CNT; //rowCnt == 한 row의 노드 수
			for(var j = 0; j < rowCnt; j++) {
				var index = rowIndex+j;
				var compTm = dlt_VIEW_OPER_PL_NODE_INFO.getCellData(i, "COMPARE_TM_"+j).split("초");
				if(parseInt(compTm[0]) > 0){
					AL0600G1.setCellColor(i, "COMPARE_TM_"+j, "#0000FF");
				}else if(parseInt(compTm[0]) < 0){
					AL0600G1.setCellColor(i, "COMPARE_TM_"+j, "#FF0000");
				}else if(parseInt(compTm[0]) == 0 || parseInt(compTm[0]) == '-'){
					AL0600G1.setCellColor(i, "COMPARE_TM_"+j, "#000000");
				}

				if(isNaN(parseInt(compTm[0])) != true){
					totalCompTm = totalCompTm + parseInt(compTm[0]);
				}
			}

			dlt_VIEW_OPER_PL_NODE_INFO.setCellData(i, "TOTAL_COMP_TM", totalCompTm + "초");
			var rowTotalCompTm = dlt_VIEW_OPER_PL_NODE_INFO.getCellData(i, "TOTAL_COMP_TM").split("초"); // 총 비교시간

			if(parseInt(rowTotalCompTm[0]) > 0){
				AL0600G1.setCellColor(i, "TOTAL_COMP_TM", "#0000FF");
			}else if(parseInt(rowTotalCompTm[0]) < 0){
				AL0600G1.setCellColor(i, "TOTAL_COMP_TM", "#FF0000");
			}else if(parseInt(rowTotalCompTm[0]) == 0 || parseInt(rowTotalCompTm[0]) == '-'){
				AL0600G1.setCellColor(i, "TOTAL_COMP_TM", "#000000");
			}
		}

		dlt_VIEW_OPER_PL_NODE_INFO.modifyAllStatus("R");
	};

