	scwin.openPopUpChart = function(){
		var operSn = dlt_VIEW_OPER_PL_NODE_INFO.getCellData(AL0600G1.getFocusedRowIndex(), "OPER_SN");

		if(com.isEmpty(operSn)){
			com.alert("운행순번을 선택하고 시공도 보기를 클릭 하시기 바랍니다.");
			return;
		}

		var jsonData = dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getAllJSON();
		var transData = new Array();
		var realData = new Array();

		for(var j=0; j<dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getRowCount(); j++){
			if(jsonData[j].OPER_SN  == dlt_VIEW_OPER_PL_NODE_INFO.getCellData(AL0600G1.getFocusedRowIndex(), "OPER_SN")){
				transData.push(dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getRowJSON(j));
				realData.push(dlt_BRT_OPER_ALLOC_RL_NODE_INFO.getRowJSON(j));
			}
		}

		var routId = dlt_BMS_ROUT_MST.getCellData(AL0600G0.getFocusedRowIndex(), "ROUT_ID");
		var dayDiv = dlt_BMS_ROUT_MST.getCellData(AL0600G0.getFocusedRowIndex(), "DAY_DIV");
		var routNm = dlt_BMS_ROUT_MST.getCellData(AL0600G0.getFocusedRowIndex(), "ROUT_NM");
		var operDt = dma_sub_search.get("OPER_DT");

		 var data = { data : routId, data2 : dayDiv, data3 : routNm, data4 : operSn, data5:transData, data6:realData, operDt : operDt, callbackFn : "" };
         var options = { id : "AL0600P0",
         				 width : "1800px",
         				 height : "900px",
                         popupName : "운행 결과 비교 시공도", 
                         modal : true};

		com.openPopup("/cm/common/popUpCompareChart.xml", options, data);
	};

	scwin.sub_AL0600G0R0_submitdone = function(e) {
		com.setFocusedCell(AL0600G0, "REP_ROUT_NM");
	};

	scwin.sbx_ChoiceBox_onviewchange = function(info) {
		if (info.newValue == "ALL") {
			autoChoiceBox.hide();
			search_input.setValue("");
			search_input.show("");
		} 
		else if (info.newValue == "REP_ROUT_NM") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_searchitem", "REP_ROUT_NM", "REP_ROUT_NM");
			autoChoiceBox.show("");
		} else if (info.newValue == "ROUT_NM") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_searchitem", "ROUT_NM", "ROUT_NM");
			autoChoiceBox.show("");
		}
	};

	scwin.AL0600G0_onrowindexchange = function(row,oldRow) {
		var focusOption = {keyMap : dma_sub_search, keyMapColumn : "DAY_DIV,ROUT_ID"};
		com.changeDualGrid(AL0600G0, AL0600G1, null, sub_OPER_ALL_REAL_NODE, focusOption, row, oldRow, "운행계획 상세");

	};

