	scwin.sub_MO0101G4R0_submitdone = function(e) {

		for(var i=0; i<dlt_BRT_DSPTCH_LOG.getDataLength(); i++) {
			var dsptchDiv = ""; 
			var dsptchConts = "";
			var contsResult = "";
			var min = "";
			var sec = "";			
			dsptchDiv =  dlt_BRT_DSPTCH_LOG.getCellData(i, "DSPTCH_DIV");
			dsptchConts = dlt_BRT_DSPTCH_LOG.getCellData(i, "DSPTCH_CONTS");

			if(parseInt(dsptchConts) != "undefined") {
				if(Math.abs(parseInt(dsptchConts) >= 60)) {
					 min = Math.abs(parseInt(dsptchConts/60)) + "분 ";
				}
				 sec = Math.abs(parseInt(dsptchConts%60)) + "초 ";

				if(dsptchDiv == "DP002") {
					if(parseInt(dsptchConts) > 0) {
						contsResult = min + sec + "느립니다."; 
					}
					else if(parseInt(dsptchConts) < 0) {
						contsResult = min + sec + "빠릅니다."; 
					}
					else if(parseInt(dsptchConts) == 0) {
						contsResult = "정상 운행중입니다.";
					}
				}

				else if(dsptchDiv == "DP003") {
					contsResult = min + sec + "정차하세요.";	
				}
			} //end ifx1

			dlt_BRT_DSPTCH_LOG.setCellData(i, "DSPTCH_CONTS", contsResult);			
		}//end for
	};

