	scwin.clearInterval = function() {
		if(sigTimeout) {
			clearInterval(sigTimeout);	
		}
	}

	function leadingZeros(n, digits) {
	  var zero = '';
	  n = n.toString();

	  if (n.length < digits) {
	    for (i = 0; i < digits - n.length; i++)
	      zero += '0';
	  }
	  return zero + n;
	}


	scwin.sub_selectSigOper_submitdone = function(e) {
		if(sigTimeout) {
			clearInterval(sigTimeout);	
		}

		sigTimeout = setTimeout(function showSig() {
			$p.executeSubmission(sub_MO0101SCK2);
			sigTimeout = setTimeout(showSig, dlt_SIG_PERIOD.getCellData(0, "SIG_PERIOD"));
		}, dlt_SIG_PERIOD.getCellData(0, "SIG_PERIOD"));
	};
