head.script:
	scwin.onpageload = function() {

		// ROUT_ID가 grid focus로 되어 있어서 Read하는데 버그있음 이거먼저 고치자

		 var autoOptions = {
			Main : { grid : MO0101G0, srchSbm : sub_MO0101G0R0, keyColumn : "VHC_ID", srchGrp : pgm_header, name : "노선 목록"}

		 };

		var map = routMap.initMap("map_MO0101", {width:"100%"
				, height:"100%"
				, onClick: scwin.onClickMap				
		});
		routMap.cronMap("map_MO0101_vehicle", {
				onMarkerClick: scwin.onClickMapVehicle				
		},map);

		routMap.cronMap("map_MO0101_sttn", {
				onMarkerClick: scwin.onClickMapSttn				
		},map);

		routMap.cronMap("map_MO0101_crs", {
				onMarkerClick: scwin.onClickMapCrs				
		},map);

		var codeOptions = [ {code : "CRS_KIND", compID : "CRS_KIND"},
		 ];			

		com.setCommonDtl(codeOptions);

		com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID, autoOptions);		

		MO0101G0.mergeCol(2,false);
		MO0101G0.mergeCells("byrestriccol");

		com.executeSubmission(sub_MO0101SHI0);	

		//connect();	

		//탭 목록 클릭 이벤트
		$(".moni_img").find("li").click( function() {
			var li_id = this.id;
			$(".moni_img").find("li").removeClass("on");
			$(".moni_img").find("#"+li_id).addClass("on");
			if(li_id == "li_busimg") {
				busActive = 1;
				sttnActive = 0;
				crossActive = 0;

				MO0101T0.show();
				MO0101T1.hide();
				MO0101T2.hide();
				if(busFocus != 0){
					MO0101G0.setFocusedCell(busFocus, "VHC_ID");
				} else if (busFocus == 0) {
					com.setFocusedCell(MO0101G0, "VHC_ID");	
				}
				scwin.MO0101G0_onrowindexchange();			
			}
			else if(li_id == "li_sttnimg") {
				busActive = 0;
				sttnActive = 1;
				crossActive = 0;

				MO0101T0.hide();
				MO0101T1.show();
				MO0101T2.hide();
				if(sttnFocus != 0){
					MO0101G1.setFocusedCell(sttnFocus, "NODE_ID");
				} else if (sttnFocus == 0) {
					com.setFocusedCell(MO0101G1, "NODE_ID");	
				}				
				scwin.MO0101G1_onrowindexchange();	
			}
			else if(li_id == "li_crossimg") {
				busActive = 0;
				sttnActive = 0;
				crossActive = 1;

				MO0101T0.hide();
				MO0101T1.hide();
				MO0101T2.show();
				if(crossFocus != 0){
					MO0101G2.setFocusedCell(crossFocus, "NODE_ID");
				} else if (crossFocus == 0) {
					com.setFocusedCell(MO0101G2, "NODE_ID");	
				}				
				scwin.MO0101G2_onrowindexchange();	
			}
		});

	};	

	var startMarker = 1;
	var busFocus = 0;
	var sttnFocus = 0;
	var crossFocus = 0;
	var busActive = 0;
	var sttnActive = 0;
	var crossActive = 0;

	function connect() {
		var socket = new SockJS('/websocket');
		stompClient = Stomp.over(socket);

		stompClient.connect({}, onConnected, onError);

	}	


	function onConnected(frame) {
		//setConnected(true);

		console.log('Connected: ' + frame);

	    // Subscribe to the Public Topic

	    stompClient.subscribe('/topic/public', scwin.onMessageReceived);

	}


	function onError(error) {
		showMessage('Could not connect to WebSocket server. ' + error);
	}	

	scwin.onMessageReceived = function(payload) {
		var jsonObj = JSON.parse(payload.body);

		var attrId = jsonObj.ATTR_ID;
		//var attrId = jsonObj.getData("ATTR_ID");

		if(attrId == 1000){
			var curIndex = dlt_BRT_CUR_OPER_INFO.getMatchedIndex("VHC_ID", jsonObj.VHC_ID);

			dlt_BRT_CUR_OPER_INFO.setRowJSON(curIndex, jsonObj, true);

			jsonObj = dlt_BRT_CUR_OPER_INFO.getDataRow(curIndex);

			var rowData = dlt_BRT_CUR_OPER_INFO.getCellData(MO0101G0.getFocusedRowIndex(), "VHC_ID")

			routMap.showVehicle2("map_MO0101", jsonObj, rowData, MO0101G0, curIndex);
		}else if(attrId == 2000){
			var index = dlt_BMS_DVC_INFO.getMatchedIndex("DVC_ID", jsonObj.DVC_ID);

			dlt_BMS_DVC_INFO.setRowJSON(index, jsonObj, true);

		}

		//showMessage(jsonObj);
	}

	function showMessage(message) {
		$("#mf_tac_layout_contents_MN00050202_body_message").append("<tr><td>" + message + "</td></tr>");
	}	



	scwin.MO0101G0_onrowindexchange = function(row,oldRow) {
		busFocus = row;		 
		com.executeSubmission(sub_MO0101G3R0);		 

	};

	scwin.sub_MO0101G0R0_submitdone = function(e) {
		com.setFocusedCell(MO0101G0, "VHC_ID");

	};

	scwin.sbx_ChoiceBox_onviewchange = function(info) {
		com.searchGrid(MO0101G0, sub_MO0101G0R0);
	};


	scwin.sub_MO0101SHI0_submitdone = function(e) {
		TYPE1.setValue("RR00000001");		
		$p.executeSubmission(sub_MO0101SHI1);
		com.searchGrid(MO0101G0, sub_MO0101G0R0);
	};

	scwin.TYPE1_onviewchange = function(info) {
		startMarker = 1;		
		$p.executeSubmission(sub_MO0101SHI1);
	};

	scwin.sub_MO0101G3R0_submitdone = function(e) {

		com.executeSubmission(sub_MO0101G4R0);
		routMap.showRoute("map_MO0101", dlt_BMS_ROUT_NODE_CMPSTN.getAllJSON(), null, "ONLYLINE");			

		if ($(".moni_img").find("#li_sttnimg").hasClass("on") || startMarker == 1) {
			routMap.initOverlay("map_MO0101_crs");
			routMap.showMarkerListTab("map_MO0101_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON(), MO0101G1.getFocusedRowIndex(), MO0101G1, MO0101T0, MO0101T1, MO0101T2);
		}

		if ($(".moni_img").find("#li_crossimg").hasClass("on") || startMarker == 1) {
			routMap.initOverlay("map_MO0101_sttn");
			routMap.showMarkerListTab("map_MO0101_crs", dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON(), MO0101G2.getFocusedRowIndex(), MO0101G2, MO0101T0, MO0101T1, MO0101T2);			
		}

		if ($(".moni_img").find("#li_busimg").hasClass("on") || startMarker == 1) {
			routMap.showVehicle("map_MO0101_vehicle", dlt_BRT_CUR_OPER_INFO.getAllJSON(),
					dlt_BRT_CUR_OPER_INFO.getCellData(MO0101G0.getFocusedRowIndex(), "VHC_ID"), MO0101G0);
		}			 					 
		startMarker = 0;					 
	};

	scwin.sub_MO0101SHI1_submitdone = function(e) {
		com.searchGrid(MO0101G0, sub_MO0101G0R0);
		com.searchGrid(MO0101G1, sub_MO0101G1R0);
		com.searchGrid(MO0101G2, sub_MO0101G2R0);
		$p.executeSubmission(sub_MO0101G3R0);			
	};

	scwin.TYPE2_onviewchange = function(info) {
		startMarker = 1;
		com.searchGrid(MO0101G0, sub_MO0101G0R0);
		com.searchGrid(MO0101G1, sub_MO0101G1R0);
		com.searchGrid(MO0101G2, sub_MO0101G2R0);
		$p.executeSubmission(sub_MO0101G3R0);			
	};


	scwin.MO0101G1_onrowindexchange = function(row,oldRow) {
		sttnFocus = row;
		com.executeSubmission(sub_MO0101G3R0);			
	};

	scwin.sub_MO0101G4R0_submitdone = function(e) {
		var focusGrid = "";
		var focusIdx = 0;

		if(busActive == 1){
			focusGrid = MO0101G0;
			focusIdx = MO0101G0.getFocusedRowIndex();
		} else if (sttnActive == 1) {
			focusGrid = MO0101G1;
			focusIdx = MO0101G1.getFocusedRowIndex();
		} else if (crossActive == 1) {
			focusGrid = MO0101G2;
			focusIdx = MO0101G2.getFocusedRowIndex();
		}

		//카테고리
//		routMap.showCategory2("map_MO0101", "map_MO0101_sttn", "map_MO0101_crs", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON(),
//								 dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON(), focusIdx, focusGrid);
		$("#map_MO0101").find("#category_map_MO0101").empty();
		var categoryClass = null;
		var categoryName = null;

		var categoryContentCross = null;
		var categoryContentNormal = null;
		var categoryContentAbnormal = null;

		var isShowCrs = routMap.mapInfo["map_MO0101_crs"].isShowCrs; 
		var isShowNormal = routMap.mapInfo["map_MO0101_sttn"].isShowNormal;
		var isShowAbnormal = routMap.mapInfo["map_MO0101_sttn"].isShowAbnormal;

		var classOnOff = null;
		categoryClass = "crossimg";
		categoryName = "교차로";	
		categoryContentCross =	'<li class="'+ isShowCrs +'">' ;
		categoryContentCross +=	'<span class="' +categoryClass +'"></span>';
		categoryContentCross +=	categoryName;
		categoryContentCross +=	'</li>';   

		categoryClass = "normal_busimg";
		categoryName = "정류장";
		categoryContentNormal =	'<li class="'+ isShowNormal +'">' ;
		categoryContentNormal +=	'<span class="' +categoryClass +'"></span>';
		categoryContentNormal +=	categoryName;
		categoryContentNormal +=	'</li>';

		categoryClass = "abnormal_busimg"
		categoryName = "고장위치";
		categoryContentAbnormal =	'<li class="'+ isShowAbnormal +'">' ;
		categoryContentAbnormal +=	'<span class="' +categoryClass +'"></span>';
		categoryContentAbnormal +=	categoryName;
		categoryContentAbnormal +=	'</li>';

		$("#map_MO0101").find("#category_map_MO0101").append(categoryContentNormal);
		$("#map_MO0101").find("#category_map_MO0101").append(categoryContentAbnormal);	
		$("#map_MO0101").find("#category_map_MO0101").append(categoryContentCross);
		//클릭 이벤트
		$("#map_MO0101").find("li").click( function() {
			var className = this.className;
			var category = $("#map_MO0101").find("#category_map_MO0101");
			var spanId = $(this).find("span");
			var spanClassName = spanId.attr("class");

			var categoryMapId = null;

			//활성화 되어있을 때
			if (className === 'on') {
				currCategory = '';
				this.className = '';

				if (spanClassName == "crossimg") {
					routMap.initNode("map_MO0101_crs");
					routMap.mapInfo["map_MO0101_crs"].isShowCrs = "";
					for(var i=0; i<dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON().length; i++){
						routMap.showMarkerTab("map_MO0101_crs", dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON()[i], i, focusIdx, focusGrid, MO0101T0, MO0101T1, MO0101T2);		
					}				
				}
				//정상 정류장
				else if(spanClassName == "normal_busimg") {
					routMap.initNode("map_MO0101_sttn");
					routMap.mapInfo["map_MO0101_sttn"].isShowNormal = "";
					for(var i=0; i<dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON().length; i++){
						routMap.showMarkerTab("map_MO0101_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON()[i], i, focusIdx, focusGrid, MO0101T0, MO0101T1, MO0101T2);		
					}						
				}
				//비정상 정류장
				else if(spanClassName == "abnormal_busimg") {
					routMap.initNode("map_MO0101_sttn");
					routMap.mapInfo["map_MO0101_sttn"].isShowAbnormal = "";
					for(var i=0; i<dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON().length; i++){
						routMap.showMarkerTab("map_MO0101_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON()[i], i, focusIdx, focusGrid, MO0101T0, MO0101T1, MO0101T2);		
					}						
				}


				//routMap.initNode(mapId);
				//routMap.removeMarkers2(mapId);

				//removeMarker();

			} else {
				this.className = 'on';
				if (spanClassName == "crossimg") {
					routMap.initNode("map_MO0101_crs");
					routMap.mapInfo["map_MO0101_crs"].isShowCrs = "on";
					for(var i=0; i<dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON().length; i++){
						routMap.showMarkerTab("map_MO0101_crs", dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON()[i], i, focusIdx, focusGrid, MO0101T0, MO0101T1, MO0101T2);		
					}					
				}
				else if(spanClassName == "normal_busimg") {
					routMap.initNode("map_MO0101_sttn");
					routMap.mapInfo["map_MO0101_sttn"].isShowNormal = "on";	
					for(var i=0; i<dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON().length; i++){
						routMap.showMarkerTab("map_MO0101_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON()[i], i, focusIdx, focusGrid, MO0101T0, MO0101T1, MO0101T2);		
					}					
				}
				else if(spanClassName == "abnormal_busimg") {
					routMap.initNode("map_MO0101_sttn");
					routMap.mapInfo["map_MO0101_sttn"].isShowAbnormal = "on";
					for(var i=0; i<dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON().length; i++){
						routMap.showMarkerTab("map_MO0101_sttn", dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON()[i], i, focusIdx, focusGrid, MO0101T0, MO0101T1, MO0101T2);		
					}					
				}

			}	

		}); //end click

	}; //end scwin.sub_MO0101G4R0_submitdone

	scwin.sub_MO0101G1R0_submitdone = function(e) {
		com.setFocusedCell(MO0101G1, "NODE_ID");		

		//정류장 상태
		for (i=0; i<dlt_BMS_ROUT_NODE_CMPSTN2.getDataLength(); i++) {
			//비정상
			if (dlt_BMS_ROUT_NODE_CMPSTN2.getCellData(i, "COND_ERROR") == "비정상") {
				MO0101G1.setCellClass(i, "COND_ERROR", "cond_abnormal");
			}
			//정상
			else if (dlt_BMS_ROUT_NODE_CMPSTN2.getCellData(i, "COND_ERROR") == "정상") {
				MO0101G1.setCellClass(i, "COND_ERROR", "cond_normal");
			}
		}			
	};

	scwin.sub_MO0101G2R0_submitdone = function(e) {
		com.setFocusedCell(MO0101G2, "NODE_ID");		
	};

	scwin.MO0101G2_onrowindexchange = function(row,oldRow) {
		crossFocus = row;
		com.executeSubmission(sub_MO0101G3R0);		
	};

	scwin.onClickMapSttn = function(index, mapId) {
		$(".moni_img").find("li").removeClass("on");

		if (mapId == "map_MO0101_sttn") {

			if(sttnActive != 1) {
				MO0101G1.setFocusedCell(index, "NODE_ID");
				scwin.MO0101G1_onrowindexchange();					
			}

			$(".moni_img").find("#li_sttnimg").addClass("on");
			busActive = 0;
			sttnActive = 1;
			crossActive = 0;
			MO0101T0.hide();
			MO0101T1.show();
			MO0101T2.hide();	
		}
	};

	scwin.onClickMapCrs = function(index, mapId) {
		$(".moni_img").find("li").removeClass("on");

		if (mapId == "map_MO0101_crs") {

			if(crossActive != 1) {
				MO0101G2.setFocusedCell(index, "NODE_ID");
				scwin.MO0101G2_onrowindexchange();					
			}			

			$(".moni_img").find("#li_crossimg").addClass("on");
			busActive = 0;
			sttnActive = 0;
			crossActive = 1;
			MO0101T0.hide();
			MO0101T1.hide();
			MO0101T2.show();			
		}			
	};
