	scwin.connect = function() {
		var socket = new SockJS('/websocket');
		stompClient = Stomp.over(socket);

		stompClient.connect({}, scwin.onConnected, scwin.onError);

	}	

	scwin.onConnected = function (frame) {
		console.log('Connected: ' + frame);

	    stompClient.subscribe('/topic/public', scwin.onMessageReceived);

	}

	scwin.onError = function (error) {
		showMessage('Could not connect to WebSocket server. ' + error);
	}			


	scwin.onMessageReceived = function(payload) {
		var jsonObj = JSON.parse(payload.body);

		var attrId = jsonObj.ATTR_ID;
		var dataList = jsonObj.LIST;
		var nodeId = dlt_BMS_ROUT_NODE_CMPSTN2.getCellData(MO0201G1.getFocusedRowIndex(), "NODE_ID");

		//attrId = 2022 : 정류소별 차량 도착 정보
		if(attrId == 2022){

		//payload.body >>>> {"ATTR_ID":2022,"LIST":[{"ROUT_ID":"RT00000080","REMAIN_TM":1364334,"REMAIN_STTN":2}],"STTN_ID":"ND00000017"}

		//jsonObj.STTN_ID => 정류소아이디
		//jsonObj.LIST => 도착정보 리스트
		// ㄴ ROUT_ID => 노선아이디
		// ㄴ REMAIN_TM => 남은시간(초)
		// ㄴ REMAIN_STTN => 몇 정류소 전

			if(attrId == 2022 && nodeId == jsonObj.STTN_ID){
				MO0201T0_setTable(dataList);
			}			
		}

		//attrId = 4014 : 실시간 차량 위치 정보
		else if(attrId == 4014){
				//payload.body >>>> {"GPS_Y":36.484005,"ATTR_ID":4014,"DVC_ID":"DV00000002","GPS_X":127.25973,"VHC_ID":"VH00000002"}		

		//jsonObj.VHC_ID => 차량아이디
		//jsonObj.GPS_X => x좌표
		//jsonObj.GPS_Y; => y좌표

		//content = jsonObj.VHC_ID + "," + jsonObj.GPS_X + ", " + jsonObj.GPS_Y;
			try {
		//		setTimeout(function() {

					var curIndexArr = dlt_BRT_CUR_OPER_INFO.getMatchedIndex("VHC_ID", jsonObj.VHC_ID);

					if(curIndexArr.length != 0) {
						dlt_BRT_CUR_OPER_INFO.setRowJSON(curIndexArr[0], jsonObj, true);

						dataRowJson = dlt_BRT_CUR_OPER_INFO.getDataRow(curIndexArr[0]);
						routMap.moveVehicle("map_MO0201_vehicle", dataRowJson, curIndexArr[0], MO0201G0.getFocusedRowIndex());									

					}
		//		},100);	
			} catch (e) {
				console.log("[scwin.onMessageReceived] Exception :: " + e.message);			
			}	
		}

	};

