	//버스 운행 현황 업데이트 함수
	scwin.updateStatusBus = function() {
		var $busStatus = $("#map_busstatus");
		var runBus = 0;
		var breakDownBus = 0;
		var operBus = 0;
		var lastBus = 0;
		var steadBus = 0;
		var fixBus = 0;
		var notOperBus = 0;
		var deadHeadBus = 0;
		var emerBus = 0;
		var acciBus = 0;

		var allBusCount = dlt_BRT_CUR_OPER_INFO.getAllJSON().length;
		$busStatus.find("#allBus").html(allBusCount);

		for(var i=0; i<allBusCount; i++) {
			//정상운행
			if(dlt_BRT_CUR_OPER_INFO.getCellData(i, "OPER_STS") == "OS001") {
				operBus++;
			}
			//막차
			else if(dlt_BRT_CUR_OPER_INFO.getCellData(i, "OPER_STS") == "OS002") {
				lastBus++;
			}
			//대차
			else if(dlt_BRT_CUR_OPER_INFO.getCellData(i, "OPER_STS") == "OS003") {
				steadBus++;
			}
			//정비
			else if(dlt_BRT_CUR_OPER_INFO.getCellData(i, "OPER_STS") == "OS004") {
				fixBus++;
			}
			//운행종료
			else if(dlt_BRT_CUR_OPER_INFO.getCellData(i, "OPER_STS") == "OS005") {
				notOperBus++;
			}
			//공차
			else if(dlt_BRT_CUR_OPER_INFO.getCellData(i, "OPER_STS") == "OS006") {
				deadHeadBus++;
			}
			//긴급
			else if(dlt_BRT_CUR_OPER_INFO.getCellData(i, "OPER_STS") == "OS007") {
				emerBus++;
			}
			//사고
			else if(dlt_BRT_CUR_OPER_INFO.getCellData(i, "OPER_STS") == "OS008") {
				acciBus++;
			}
		}

//		$busStatus.find("#runBus").html(runBus);
//		$busStatus.find("#breakDownBus").html(breakDownBus);
		$busStatus.find("#operBus").text(operBus);
		$busStatus.find("#lastBus").text(lastBus);
		$busStatus.find("#steadBus").text(steadBus);
		$busStatus.find("#fixBus").text(fixBus);
		$busStatus.find("#notOperBus").text(notOperBus);
		$busStatus.find("#deadHeadBus").text(deadHeadBus);
		$busStatus.find("#emerBus").text(emerBus);
		$busStatus.find("#acciBus").text(acciBus);

	}

	//정류소 현황 업데이트 함수
	scwin.updateStatusSttn = function() {
		var $sttnStatus = $("#map_sttnstatus");
		var runSttn = 0;
		var breakDownSttn = 0;

		var allSttnCount = dlt_BMS_ROUT_NODE_CMPSTN2.getAllJSON().length;
		$sttnStatus.find("#allSttn").html(allSttnCount);

		for(var i=0; i<allSttnCount; i++) {
			//정상
			if(dlt_BMS_ROUT_NODE_CMPSTN2.getCellData(i, "COND_ERROR") == "CE002") {
				runSttn++;	
			}
			//고장
			else if(dlt_BMS_ROUT_NODE_CMPSTN2.getCellData(i, "COND_ERROR") == "CE001") {
				breakDownSttn++;
			}

		}

		$sttnStatus.find("#runSttn").html(runSttn);
		$sttnStatus.find("#breakDownSttn").html(breakDownSttn);

	}

	//교차로 현황 업데이트 함수
	scwin.updateStatusCrs = function() {
		var $crsStatus = $("#map_crsstatus");
		var runCrs = 0;
		var breakDownCrs = 0;

		var allCrsCount = dlt_BMS_ROUT_NODE_CMPSTN3.getAllJSON().length;
		$crsStatus.find("#allCrs").html(allCrsCount);

		for(var i=0; i<allCrsCount; i++) {
			//정상
			//if(dlt_BMS_ROUT_NODE_CMPSTN3.getCellData(i, "CTR_STS") == "CT001") {
			if(dlt_BMS_ROUT_NODE_CMPSTN3.getCellData(i, "COND_ERROR") == "CE002") {
				runCrs++;	
			}
			//고장 또는 네트워크 오류
			else if(dlt_BMS_ROUT_NODE_CMPSTN3.getCellData(i, "COND_ERROR") == "CE001" || dlt_BMS_ROUT_NODE_CMPSTN3.getCellData(i, "COND_ERROR") == "CE003") {
				breakDownCrs++;
			}

		}

		$crsStatus.find("#runCrs").html(runCrs);
		$crsStatus.find("#breakDownCrs").html(breakDownCrs);

	}

	//정류소별 차량 도착 정보
	scwin.vhcArriInfo = function(dataList) {
		var remainTmMin = "";	
		var str = "";

		if(dataList != null && dataList.length != 0){		
			for(var i = 0; i < dataList.length; i++) {
				//초 -> 분으로 변경. 0분 이하면 잠시후 도착
				remainTmMin = parseInt((dataList[i].REMAIN_TM%3600)/60) + '분';

				if(remainTmMin == '0분'){
					remainTmMin = '곧도착';
				}

				str += '<tr>' 
				str += '<th style="font-size: 12px; text-align: center; padding: 5px;">' 
				str += '<a href="javascript:void(0)" onclick="fn_showBusLine(&quot;118900006&quot;);" style="color: black">'
				str += '<strong>' + dataList[i].ROUT_NM
				str += '</strong>' 
				str += '<br>' 
				str += '</a>' 
				str += '</th>' 
