var oriBgWidth = null;
var oriBgHeight = null;
var oriFcltWidth = [];
var oriFcltHeight = [];
scwin.onpageload = function() {
	scwin.param = com.getParameter();
	dma_sub_search.set("NODE_ID", scwin.param.NODE_ID);
	dma_sub_search.set("NODE_NM", scwin.param.NODE_NM);
	dma_sub_search.set("STTN_FCLT_TYPE", scwin.param.STTN_FCLT_TYPE);
	scwin.closeCallbackFncName = scwin.param.callbackFn;

	var codeOptions = [ {code : "NODE_TYPE", compID : "MO0203P0:NODE_TYPE" },
						{code : "PARAM_KIND", compID : "PARAM_KIND"},
						{code : "INST_LOC", compID : "MO0203P0:INST_LOC"}
	 ];			

	com.setCommonDtl(codeOptions);


	com.executeSubmission(sub_MO0203SHI1);



};

scwin.onpageunload = function() {

};

scwin.btn_cancel_onclick = function() {
	com.closePopup();		
};

scwin.sub_MO0203P0R0_submitdone = function(e) {
	com.executeSubmission(sub_MO0203P0R1);

	var sCode = com.getResultCode(e);
	if (sCode == gcm.MESSAGE_CODE.STATUS_SUCCESS) {

		if(dma_sub_search.get("STTN_FCLT_TYPE") == 'ST003'){
			document.getElementById(STTN_IMG.getID()).className = "ST003_station";
		}else{
			document.getElementById(STTN_IMG.getID()).className = 'ST002_station_1';
		}	
	}

	MO0203P0.setFocusedCell(0, "FCLT_NM");
};

scwin.MO0203P0_onrowindexchange = function(row,oldRow) {
	var param = com.getParameter();

	var str = ""
	var imgSrc = ""
	var imgId = ""

	for (i=0; i<dlt_BMS_FCLT_INFO.getDataLength(); i++) {
		var coord = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COORDS").split(',');
		var top = coord[0];
		var left = coord[1];
		var fcltKind = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_KIND");
		var fcltId = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_ID");
		var fcltCond = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COND");

		if((fcltKind == 'FK001' && fcltCond == 'FC020')|| fcltCond == ''){
			imgSrc = "/cm/images/bms/BIT_RED_normal.png";
		}else if((fcltKind == 'FK001' && fcltCond == 'FC021') || fcltCond == ''){
			imgSrc = "/cm/images/bms/BIT_RED_abnormal.png";
		}else if((fcltKind == 'FK004' && fcltCond == 'FC020') || fcltCond == ''){
			imgSrc = "/cm/images/bms/AIRTUBE_normal.png";
		}else if((fcltKind == 'FK004' && fcltCond == 'FC021') || fcltCond == ''){
			imgSrc = "/cm/images/bms/AIRTUBE_abnormal.png";
		}else if((fcltKind == 'FK005' && fcltCond == 'FC020') || fcltCond == ''){
			imgSrc = "/cm/images/bms/AIRCON_normal.png";
		}else if((fcltKind == 'FK005' && fcltCond == 'FC021') || fcltCond == ''){
			imgSrc = "/cm/images/bms/AIRCON_abnormal.png";
		}

		str += '<div id="'+fcltId+'"style="position: absolute; top:'+top+'px; left: '+left+'px;" class="w2group fclt_img" onclick="imgOnclick(this.id);">';
		str += '<img xmlns="http://www.w3.org/1999/xhtml" style="" src="'+imgSrc+'" id="'+fcltId+'img'+'">';
		str += '</div>'

	}

	$('#'+STTN_IMG.getID()).html(str);

	var fcltID = dlt_BMS_FCLT_INFO.getCellData(row, "FCLT_ID");
	var fcltKind = dlt_BMS_FCLT_INFO.getCellData(row, "FCLT_KIND");
	var fcltCond = dlt_BMS_FCLT_INFO.getCellData(row, "FCLT_COND");

	//이미지 변경
	if((fcltKind == 'FK001' && fcltCond == 'FC020')|| fcltCond == ''){
			$('#'+fcltID).children('img').attr("src", '/cm/images/bms/BIT_RED_normal_selected.png');
		}else if((fcltKind == 'FK001' && fcltCond == 'FC021')|| fcltCond == ''){
			$('#'+fcltID).children('img').attr("src", '/cm/images/bms/BIT_RED_abnormal_selected.png');
		}else if((fcltKind == 'FK004' && fcltCond == 'FC020') || fcltCond == ''){
			$('#'+fcltID).children('img').attr("src", '/cm/images/bms/AIRTUBE_normal_selected.png');
		}else if((fcltKind == 'FK004' && fcltCond == 'FC021') || fcltCond == ''){
			$('#'+fcltID).children('img').attr("src", '/cm/images/bms/AIRTUBE_abnormal_selected.png');
		}else if((fcltKind == 'FK005' && fcltCond == 'FC020')|| fcltCond == ''){
			$('#'+fcltID).children('img').attr("src", '/cm/images/bms/AIRCON_normal_selected.png');
		}else if((fcltKind == 'FK005' && fcltCond == 'FC021')|| fcltCond == ''){
			$('#'+fcltID).children('img').attr("src", '/cm/images/bms/AIRCON_abnormal_selected.png');
		}

	oriBgWidth = document.getElementById(STTN_IMG.getID()).offsetWidth;
	oriBgHeight = document.getElementById(STTN_IMG.getID()).offsetHeight;				     	
};

//동적 그리드 함수
scwin.setGridViewInfo = function() {
	var gridHeader ="", gridBody=""; 
	var rowData = dlt_BMS_FCLT_INFO.getAllJSON();
	var paramKindJson = dlt_PARAM_KIND.getAllJSON();
	var rowCntParamKind = dlt_PARAM_KIND.getRowCount();

	//헤더 시작
	gridHeader +='<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h_colunm_5" value="시설물상태" blockSelect="false" displayMode="label"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h_colunm_3" value="시설물ID" blockSelect="false" displayMode="label" sortable="true"></w2:column>';
	gridHeader +='<w2:column removeBorderStyle="false" width="170" inputType="text" style="height:20px" id="h_colunm_4" value="시설물종류" blockSelect="false" displayMode="label"></w2:column>';

	//동적 헤더 시작
	for(var i = 0; i < rowCntParamKind; i++) {
		var paramKind = dlt_PARAM_KIND.getCellData(i, "PARAM_KIND");

		if(paramKind != '' && paramKind != 'PK001'){
			var dlCd = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD");
			var dlCdNm = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD_NM");

			gridHeader +='<w2:column removeBorderStyle="false" width="110" inputType="text" style="height:20px" id="h_colunm'+i+3+'" value="'+dlCdNm+'" blockSelect="false" displayMode="label"></w2:column>';
			gridHeader +='<w2:column removeBorderStyle="false" width="110" inputType="text" style="height:20px" id="h_colunm'+i+4+'" value="'+dlCdNm+'자동'+'" blockSelect="false" displayMode="label"></w2:column>';
		}

	}

	//바디 시작
	gridBody += '<w2:column removeBorderStyle="false" width="117" inputType="select" style="height:20px" id="FCLT_COND" value="" blockSelect="false" displayMode="label"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="FCLT_ID" value="" blockSelect="false" displayMode="label"></w2:column>';
	gridBody += '<w2:column removeBorderStyle="false" width="117" inputType="select" style="height:20px" id="FCLT_KIND" value="" blockSelect="false" displayMode="label"></w2:column>';

	//동적 바디 시작
	for(var i = 0; i < rowCntParamKind; i++) {
		var paramKind = dlt_PARAM_KIND.getCellData(i, "PARAM_KIND");
		var dlCd = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD");
		var dlCdNm = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD_NM");
		var displayForMatter = '';

		if(dlCd == 'PK002' || dlCd == 'PK003' || dlCd == 'PK006' || dlCd == 'PK007'){
			displayForMatter = 'scwin.onOffDisFormatter'; //데이터유형이 ON/OFF인 경우 displayformaatter 추가
		}

		if(paramKind != '' && paramKind != 'PK001'){
			gridBody +='<w2:column removeBorderStyle="false" width="113" inputType="text" style="height:20px" id="'+paramKind+'" value="" blockSelect="false" displayMode="label" displayFormatter="'+displayForMatter+'"></w2:column>';
			gridBody +='<w2:column removeBorderStyle="false" width="113" inputType="text" style="height:20px" id="'+paramKind+'_AUTO_YN'+'" value="" blockSelect="false" displayMode="label" displayFormatter="scwin.AutoDisFormatter"></w2:column>';
		}
	}

	//gridView 정보 정의 
	//xml 형식을 정의 하면 <w2:gridView 에 xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" 포함 합니다.
	var gridStr = '<w2:gridView xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" '
						+'autoFit="lastColumn" class="cm_grid left" dataList="data:dlt_BMS_FCLT_INFO" ev:oncellclick="scwin.MO0203P0_oncellclick" '
						+'ev:onrowindexchange="scwin.MO0203P0_onrowindexchange" fixedColumnWithHidden="true" id="MO0203P0" ignoreCellClick="false" rowNumHeaderValue="No." rowNumVisible="true" '
						+'rowStatusVisible="false" rowStatusWidth="40" scrollByColumn="false" style="height:250px" useShiftKey="true" autoFitMinWidth="0" readOnly="true" '
						+'sortable="false" focusMode="row" showSortableImage="true" sortEvent="onclick" ev:onheaderclick="scwin.MO0203P0_onheaderclick">';
	gridStr += '<w2:header style="" id="header2">'
	+ '<w2:row style="" id="row3">'
	+ gridHeader
	+'<w2:column removeBorderStyle="false" width="170" inputType="text" style="height:20px" id="h_colunm_5" value="" blockSelect="false" displayMode="label"></w2:column>'
	+'</w2:row>'
	+'</w2:header>'
	+'<w2:gBody style="" id="gBody2">'
	+'<w2:row id="row2" style="">'
	+ gridBody
	+'<w2:column removeBorderStyle="false" width="117" inputType="text" style="height:20px" id="" value="" blockSelect="false" displayMode="label"></w2:column>'
	+'</w2:row>'
	+'</w2:gBody>';
	//gridStr += '<w2:footer id="footer1" style=""><w2:row id="row3" style=""><w2:column blockSelect="false" colSpan="30" displayMode="label" expression="&quot;데이터 개수 : &quot; + COUNT(\'COMP_ID\')" id="column42" inputType="expression" removeBorderStyle="false" style="height:20px;" textAlign="left" width="2386"></w2:column></w2:row></w2:footer>';
	gridStr += '</w2:gridView>';

	MO0203P0.setGridStyle(WebSquare.xml.parse(gridStr , true ));
	//dlt_BMS_FCLT_COND_PARAM_INFO.removeAll();
	//autoOptions.Sub1.grid = MO0203P0;
	scwin.commonCode();

	//이미지 생성 함수
	scwin.makeImage();

};

scwin.makeImage = function(){
	var str = ""
	var imgSrc = ""
	var imgId = ""

	//var param = com.getParameter();

	//시설물상태에 따라 클래스(백그라운드 색) 설정
	for (i=0; i<dlt_BMS_FCLT_INFO.getDataLength(); i++) {
		//비정상
		if (dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COND") == "FC021") {
			MO0203P0.setCellClass(i, "FCLT_COND", "cond_abnormal");
		}
		//정상
		else if (dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COND") == "FC020") {
			MO0203P0.setCellClass(i, "FCLT_COND", "cond_normal");
		}
	}

	for (i=0; i<dlt_BMS_FCLT_INFO.getDataLength(); i++) {
		var coord = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COORDS").split(',');
		var top = coord[0];
		var left = coord[1];
		var fcltKind = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_KIND");
		var fcltId = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_ID");
		var fcltCond = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COND");

		if((fcltKind == 'FK001' && fcltCond == 'FC020')|| fcltCond == ''){
			imgSrc = "/cm/images/bms/BIT_RED_normal.png";
		}else if((fcltKind == 'FK001' && fcltCond == 'FC021') || fcltCond == ''){
			imgSrc = "/cm/images/bms/BIT_RED_abnormal.png";
		}else if((fcltKind == 'FK004' && fcltCond == 'FC020') || fcltCond == ''){
			imgSrc = "/cm/images/bms/AIRTUBE_normal.png";
		}else if((fcltKind == 'FK004' && fcltCond == 'FC021') || fcltCond == ''){
			imgSrc = "/cm/images/bms/AIRTUBE_abnormal.png";
		}else if((fcltKind == 'FK005' && fcltCond == 'FC020') || fcltCond == ''){
			imgSrc = "/cm/images/bms/AIRCON_normal.png";
		}else if((fcltKind == 'FK005' && fcltCond == 'FC021') || fcltCond == ''){
			imgSrc = "/cm/images/bms/AIRCON_abnormal.png";
		}

		//시설물 이미지
		str += '<div id="'+fcltId+'" style="z-index: 1; position: absolute; top:'+top+'px; left: '+left+'px;" class="w2group fclt_img" onclick="imgOnclick(this.id);">';
		str += '<img xmlns="http://www.w3.org/1999/xhtml" style="" src="'+imgSrc+'" id="'+fcltId+'img'+'">';
		str += '</div>'

	}

	//정류소 이미지
	if(dma_sub_search.get() == 'ST003'){
		var imgSrc = "/cm/images/bms/ST003_1.png"
	}else{
		var imgSrc = "/cm/images/bms/ST002_2.jpg"
	}

	var nodeId = dma_sub_search.get("NODE_ID");

	str += '<img xmlns="http://www.w3.org/1999/xhtml" style="position:absolute; cursor:default" src="'+imgSrc+'" id="'+nodeId+'img'+'">';

	$('#'+STTN_IMG.getID()).html(str);


	var fclt_ctrindex = MO0203P0.getColumnIndex("FCLT_CTR");
	//var param = com.getParameter();

	for(var i=0; i<dlt_BMS_FCLT_INFO.getDataLength(); i++){
		var ctr_yn = dlt_BMS_FCLT_INFO.getCellData(i, "CTR_YN");
		if(ctr_yn == 'N'){

			//MO0203P0.setDisabled("cell", i, "FCLT_CTR", true);
            //제어 버튼 안보이게 처리
            MO0203P0.setCellClass(i, "FCLT_CTR", "hideData");
		}
	}

	/*oriBgWidth = document.getElementById(STTN_IMG.getID()).offsetWidth;
	oriBgHeight = document.getElementById(STTN_IMG.getID()).offsetHeight;

	for(var i = 0; i < dlt_BMS_FCLT_INFO.getRowCount(); i++){
		var fcltId = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_ID");
		oriFcltWidth[i] = document.getElementById(fcltId+'img').width;
		oriFcltHeight[i] = document.getElementById(fcltId+'img').height;
	}*/
}	

scwin.commonCode = function(){
	var codeOptions = [ 
						{code : "FCLT_KIND", compID : "MO0203P0:FCLT_KIND"}
						,{code : "FCLT_COND", compID : "MO0203P0:FCLT_COND"}
	 ];			

	com.setCommonDtl(codeOptions);
}	

function imgOnclick(id) {
	var rowIndex = dlt_BMS_FCLT_INFO.getMatchedIndex("DVC_ID", id);
	MO0203P0.setFocusedCell(rowIndex, "DVC_ID", false);
}	

