					dlt_BRT_CUR_OPER_INFO.setCellData(i, "REP_ROUT_NM", "운행종료");
				}

				/*switch(oper_sts) {
					case "OS001":
						MO0204G0.setCellClass(i, "OPER_STS", "cond_normal");
						break;	
					case "":
						dlt_BRT_CUR_OPER_INFO.setCellData(i, "OPER_STS", "운행종료");
						dlt_BRT_CUR_OPER_INFO.modifyRowStatus(i, "R");
						break;
					}	//end switch
				*/
			} //end for
		} //end if
		else {
			dma_sub_search.set("ROUT_ID", null);
			dma_sub_search.set("VHC_ID", null);
		}

		dlt_BRT_CUR_OPER_INFO.modifyAllStatus("R");

	};

