	//이미지 생성 함수
	scwin.makeImage = function(){
		var param = com.getParameter();
		//장치상태에 따라 클래스(백그라운드 색) 설정
		for (i=0; i<dlt_BMS_DVC_INFO.getDataLength(); i++) {
			//비정상
			if (dlt_BMS_DVC_INFO.getCellData(i, "DVC_COND") == "DC021") {
				MO0204G1.setCellClass(i, "DVC_COND", "cond_abnormal");
			}
			//정상
			else if (dlt_BMS_DVC_INFO.getCellData(i, "DVC_COND") == "DC020") {
				MO0204G1.setCellClass(i, "DVC_COND", "cond_normal");
			}
		}

		var topCoords = null;
		var leftCoords = null;
		var str = "";
		//$("#mf_tac_layout_contents_"+ param.menuCode+ "_body_busImage").empty();

		for(var i=0; i<dlt_BMS_DVC_INFO.getDataLength(); i++) {

			var imgSrc = null;
			var ctr_yn = dlt_BMS_DVC_INFO.getCellData(i, "CTR_YN");
			if (ctr_yn == "N" || ctr_yn == "") {
				MO0204G1.setDisabled("cell", i, "DVC_CTR", true);
				//제어 버튼 안보이게 처리
				MO0204G1.setCellClass(i, "DVC_CTR", "hideData");
				MO0204G1.setCellClass(i, "PK001", "hideData");
			}

			var dvc_kind = dlt_BMS_DVC_INFO.getCellData(i, "DVC_KIND");
			var dvc_cond = dlt_BMS_DVC_INFO.getCellData(i, "DVC_COND");
			var coordsData = dlt_BMS_DVC_INFO.getCellData(i, "DVC_COORDS");

			if(coordsData != null && coordsData != "") {			
				switch (dvc_kind){
					case "DK001":
						imgSrc = "/cm/images/bms/OBE_normal.png";
						if(dvc_cond == "DC021")
							imgSrc = "/cm/images/bms/OBE_abnormal.png";					
						break;
					case "DK002":
						imgSrc = "/cm/images/bms/drvTerminal_normal.png";
						if(dvc_cond == "DC021")
							imgSrc = "/cm/images/bms/drvTerminal_abnormal.png";
						break;
					case "DK003":
						imgSrc = "/cm/images/bms/psgGuide_normal.png";
						if(dvc_cond == "DC021")
							imgSrc = "/cm/images/bms/psgGuide_abnormal.png";
						break;
					case "DK004":
						imgSrc = "/cm/images/bms/routMap_normal.png";
						if(dvc_cond == "DC021")
							imgSrc = "/cm/images/bms/routMap_abnormal.png";
						break;
					case "DK005":
						imgSrc = "/cm/images/bms/keyPad_normal.png";
						if(dvc_cond == "DC021")
							imgSrc = "/cm/images/bms/keyPad_abnormal.png";
						break;

					//행선지 정면
					case "DK006":
					case "DK007":
						imgSrc = "/cm/images/bms/desGuide_normal.png";
						if(dvc_cond == "DC021")
							imgSrc = "/cm/images/bms/desGuide_abnormal.png";
						break;
					//행선지 측면	
					case "DK008":
					case "DK009":
					case "DK012":
						imgSrc = "/cm/images/bms/desGuide_normal.png";
						if(dvc_cond == "DC021")
							imgSrc = "/cm/images/bms/desGuide_abnormal.png";
						break;
					//행선지 후면	
					case "DK013":
						imgSrc = "/cm/images/bms/desGuide_normal.png";
					if(dvc_cond == "DC021")
							imgSrc = "/cm/images/bms/desGuide_abnormal.png";	
						break;
					//태그리스	
					case "DK011":
						imgSrc = "/cm/images/bms/tag_normal.png";
						if(dvc_cond == "DC021")
							imgSrc = "/cm/images/bms/tag_abnormal.png";
						break;				
				}
			}

			if (imgSrc != null && imgSrc != ""){

				if(coordsData != null && coordsData != "") {
					var coordsSplit = coordsData.split(",");
					var topCoords = coordsSplit[0];
					var leftCoords = coordsSplit[1];
				}
				var imgId = dlt_BMS_DVC_INFO.getCellData(i, "DVC_ID");

				str += "<div id='"+ imgId+ "' style='z-index: 1; position:absolute; top:"+topCoords+ "px; left:"+leftCoords+"px;' class='w2group' onclick='imgOnclick(this.id);'>"
				+ "<img xmlns='http://www.w3.org/1999/xhtml' src='"+ imgSrc+ "'>"
				+ "</div>";
			} // end if

		} // end for
		//$("#mf_tac_layout_contents_"+ param.menuCode+ "_body_busImage").append(str);

		var imgSrc = null;
		var vhcKind = dlt_BRT_CUR_OPER_INFO.getCellData(MO0204G0.getFocusedRowIndex(), "VHC_KIND");
		if (vhcKind == "VK001") {
			imgSrc = "/cm/images/bms/CNG_front_bus.png";	
		} else if (vhcKind == "VK002") {
			imgSrc = "/cm/images/bms/woojin_front_bus.png";	
		} else if (vhcKind == "VK003") {
			imgSrc = "/cm/images/bms/normal_front_bus.png";	
		} else {
			imgSrc = "/cm/images/bms/normal_front_bus.png";	
		}

		var vhcId = dlt_BRT_CUR_OPER_INFO.getCellData(MO0204G0.getFocusedRowIndex(), "VHC_ID");
		str += '<img xmlns="http://www.w3.org/1999/xhtml" style="position:absolute; cursor:default" src="'+imgSrc+'" id="'+vhcId+'img'+'">';

		$('#'+busImage.getID()).html(str);
	}

