				var imgId = dlt_BMS_DVC_INFO.getCellData(i, "DVC_ID");

				var str = "";
				str = "<div id='"+ imgId+ "' style='z-index:1;position:absolute; top:"+topCoords+ "px; left:"+leftCoords+"px;' class='w2group' onclick='imgOnclick(this.id);'>"
				+ "<img xmlns='http://www.w3.org/1999/xhtml' src='"+ imgSrc+ "'>"
				+ "</div>";
				$("#mf_tac_layout_contents_"+ param.menuCode+ "_body_busImage").append(str);				
			} // end if

		} // end for

		var imgSrc = null;
		var vhcKind = dlt_BRT_CUR_OPER_INFO.getCellData(MO0204G0.getFocusedRowIndex(), "VHC_KIND");
		if (vhcKind == "VK001") {
			imgSrc = "/cm/images/bms/CNG_front_bus.png";	
		} else if (vhcKind == "VK002") {
			imgSrc = "/cm/images/bms/woojin_front_bus.png";	
		} else if (vhcKind == "VK003") {
			imgSrc = "/cm/images/bms/normal_front_bus.png";	
		} else {
			imgSrc = "/cm/images/bms/normal_front_bus.png";	
		}

		var vhcId = dlt_BRT_CUR_OPER_INFO.getCellData(MO0204G0.getFocusedRowIndex(), "VHC_ID");
		str += '<img xmlns="http://www.w3.org/1999/xhtml" style="position:absolute; cursor:default" src="'+imgSrc+'" id="'+vhcId+'img'+'">';

		$("#mf_tac_layout_contents_"+ param.menuCode+ "_body_busImage").append(str);

	  var selectedDvcKind = dlt_BMS_DVC_INFO.getCellData(row, "DVC_KIND");
	  var dvc_cond = dlt_BMS_DVC_INFO.getCellData(row, "DVC_COND");
	  switch (selectedDvcKind){
			case "DK001":
				selectImg = "/cm/images/bms/OBE_normal_selected.PNG";
			//	selectImg = "/cm/images/bms/bad_icon.PNG";
				if(dvc_cond == "DC021")
					selectImg = "/cm/images/bms/OBE_abnormal_selected.png";
				break;	
			case "DK002":
				selectImg = "/cm/images/bms/drvTerminal_normal_selected.png";
			//	selectImg = "/cm/images/bms/bad_icon.PNG";
				if(dvc_cond == "DC021")
					selectImg = "/cm/images/bms/drvTerminal_abnormal_selected.png";
				break;
			case "DK003":
				selectImg = "/cm/images/bms/psgGuide_normal_selected.png";
		//		selectImg = "/cm/images/bms/bad_icon.PNG";
				if(dvc_cond == "DC021")
					selectImg = "/cm/images/bms/psgGuide_abnormal_selected.png";
				break;
			case "DK004":
				selectImg = "/cm/images/bms/routMap_normal_selected.png";
		//		selectImg = "/cm/images/bms/bad_icon.PNG";
				if(dvc_cond == "DC021")
					selectImg = "/cm/images/bms/routMap_abnormal_selected.png";
				break;
			case "DK005":
				selectImg = "/cm/images/bms/keyPad_normal_selected.png";
		//		selectImg = "/cm/images/bms/bad_icon.PNG";
				if(dvc_cond == "DC021")
					selectImg = "/cm/images/bms/keyPad_abnormal_selected.png";
				break;

			//행선지 정면
			case "DK006":
			case "DK007":
				selectImg = "/cm/images/bms/desGuide_normal_selected.png";
		//		selectImg = "/cm/images/bms/bad_icon.PNG";
				if(dvc_cond == "DC021")
					selectImg = "/cm/images/bms/desGuide_abnormal_selected.png";
				break;
			//행선지 측면	
			case "DK008":
			case "DK009":
			case "DK012":
				selectImg = "/cm/images/bms/desGuide_normal_selected.png";
		//		selectImg = "/cm/images/bms/bad_icon.PNG";
				if(dvc_cond == "DC021")
					selectImg = "/cm/images/bms/desGuide_abnormal_selected.png";
				break;
			//행선지 후면	
			case "DK013":
				selectImg = "/cm/images/bms/desGuide_normal_selected.png";
		//		selectImg = "/cm/images/bms/bad_icon.PNG";
				if(dvc_cond == "DC021")
					selectImg = "/cm/images/bms/desGuide_abnormal_selected.png";
				break;
