				if(dvc_cond == "DC021")
					selectImg = "/cm/images/bms/tag_abnormal_selected.png";
				break;		
		}      

      //이미지 변경
      var dvcId = dlt_BMS_DVC_INFO.getCellData(row, "DVC_ID");
      $('#'+dvcId).children('img').attr("src", selectImg);		
	};

