	scwin.sub_MO0205G2R0_submitdone = function(e) {
		dlt_BMS_AIRCON_FCLT_INFO.removeAll();
		dlt_BMS_SCRDOOR_FCLT_INFO.removeAll();
		dlt_BMS_AIRTUBE_FCLT_INFO.removeAll();
		dlt_BMS_PLECNT_FCLT_INFO.removeAll();

		var rowCnt = dlt_BMS_FCLT_INFO.getRowCount();

		for(var x = 0; x < rowCnt; x++){
			var fcltKind = dlt_BMS_FCLT_INFO.getCellData(x, "FCLT_KIND");

			switch (fcltKind) {
			case "FK005": //에어컨
				dlt_BMS_AIRCON_FCLT_INFO.setRowJSON(x, dlt_BMS_FCLT_INFO.getRowJSON(x), false);
				break;

			case "FK007": //스크린도어
				dlt_BMS_SCRDOOR_FCLT_INFO.setRowJSON(x, dlt_BMS_FCLT_INFO.getRowJSON(x), false);
				break;

			case "FK004": //에어튜브
				dlt_BMS_AIRTUBE_FCLT_INFO.setRowJSON(x, dlt_BMS_FCLT_INFO.getRowJSON(x), false);
				break;

			case "FK006": //승객감지장치
				dlt_BMS_PLECNT_FCLT_INFO.setRowJSON(x, dlt_BMS_FCLT_INFO.getRowJSON(x), false);
				break;

			}
		}


		com.executeSubmission(sub_MO0205G0R1);
	};

	scwin.sub_MO0205G0R1_submitdone = function(e) {
		//동적 그리드 시작
		scwin.setGridViewInfo();

		var index = dlt_BMS_FCLT_INFO.getRowCount();
		for(var x = 0; x < index; x++){
			var fcltKind = dlt_BMS_FCLT_INFO.getCellData(x, "FCLT_KIND");

			switch (fcltKind) {
			case "FK005":
				var pk002 = dlt_BMS_AIRCON_FCLT_INFO.getCellData(x, "PK002");
				if(pk002 != '' && pk002 != null){
					dlt_BMS_AIRCON_FCLT_INFO.setCellData(x, "PK030", "1");
				}else{
					dlt_BMS_AIRCON_FCLT_INFO.setCellData(x, "PK030", "0");
				}

				//통신에러 - class추가
				if (dlt_BMS_AIRCON_FCLT_INFO.getCellData(x, "PK030") == "0") {
					MO0205G2.setCellClass(x, "PK030", "cond_abnormal");
				}
				//정상 - class추가
				else if (dlt_BMS_AIRCON_FCLT_INFO.getCellData(x, "PK030") == "1") {
					MO0205G2.setCellClass(x, "PK030", "cond_normal");
				}
				break;

			}
		}
	};

	//동적 그리드 함수
	scwin.setGridViewInfo = function() {
		var gridId = "";
		var dataList = "";
		var fcltKind = ["FK005", "FK007","FK004","FK006"]; //에어컨, 스크린도어, 에어튜브, 승객감지장치

		for(var x = 0; x < fcltKind.length; x++){
			switch (fcltKind[x]) {
			case "FK005":
				gridId = "MO0205G2" 
				dataList = "dlt_BMS_AIRCON_FCLT_INFO"
				break;
			case "FK007":
				gridId = "MO0205G3" 
				dataList = "dlt_BMS_SCRDOOR_FCLT_INFO"
				break;
			case "FK004":
				gridId = "MO0205G4"
				dataList = "dlt_BMS_AIRTUBE_FCLT_INFO"
				break;
			case "FK006":
				gridId = "MO0205G5"
				dataList = "dlt_BMS_PLECNT_FCLT_INFO"
				break;
			}

			var gridHeader ="", gridBody=""; 
			var rowData = dlt_BMS_FCLT_INFO.getAllJSON();
			var paramKindJson = dlt_PARAM_KIND.getAllJSON();
			var rowCntParamKind = dlt_PARAM_KIND.getRowCount();

			//헤더 시작
			gridHeader +='<w2:column removeBorderStyle="false" width="80" inputType="text" style="height:20px" id="h_colunm_3" value="시설물ID" blockSelect="false" displayMode="label" sortable="true"></w2:column>';
			//gridHeader +='<w2:column removeBorderStyle="false" width="120" inputType="text" style="height:20px" id="h_colunm_4" value="시설물종류" blockSelect="false" displayMode="label"></w2:column>';

			//동적 헤더 시작
			for(var i = 0; i < rowCntParamKind; i++) {
				var headerWith = "70";
				var paramKind = dlt_PARAM_KIND.getCellData(i, "PARAM_KIND");
				var fcltKindParam = dlt_PARAM_KIND.getCellData(i, "FCLT_KIND");

				if(paramKind != '' && fcltKind[x] == fcltKindParam){
					var dlCd = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD");
					var dlCdNm = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD_NM");

					if(dlCd == "PK041" || dlCd == "PK043"){
						headerWith = "150";
					}

					gridHeader +='<w2:column removeBorderStyle="false" width="'+headerWith+'" inputType="text" style="height:20px" id="h_colunm'+i+3+'" value="'+dlCdNm+'" blockSelect="false" displayMode="label"></w2:column>';
					//gridHeader +='<w2:column removeBorderStyle="false" width="110" inputType="text" style="height:20px" id="h_colunm'+i+4+'" value="'+dlCdNm+'자동'+'" blockSelect="false" displayMode="label"></w2:column>';
				}

			}

			//바디 시작
			gridBody += '<w2:column removeBorderStyle="false" width="94" inputType="text" style="height:20px" id="FCLT_ID" value="" blockSelect="false" displayMode="label"></w2:column>';
			//gridBody += '<w2:column removeBorderStyle="false" width="117" inputType="select" style="height:20px" id="FCLT_KIND" value="" blockSelect="false" displayMode="label"></w2:column>';

			//동적 바디 시작
			for(var i = 0; i < rowCntParamKind; i++) {
				var paramKind = dlt_PARAM_KIND.getCellData(i, "PARAM_KIND");
				var dlCd = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD");
				var dlCdNm = dlt_commonDtlPARAM_KIND.getMatchedColumnData("DL_CD", paramKind, "DL_CD_NM");
				var fcltKindParam = dlt_PARAM_KIND.getCellData(i, "FCLT_KIND");
				var displayForMatter = '';

				if(dlCd == 'PK002' || dlCd == 'PK003' || dlCd == 'PK006' || dlCd == 'PK007'){
					displayForMatter = 'scwin.onOffDisFormatter'; //데이터유형이 ON/OFF인 경우 displayformaatter 추가
				}else if(dlCd == 'PK025'){
					displayForMatter = 'scwin.openCloseDisFormatter'; //데이터유형이 문 열림/닫힘인 경우 displayformaatter 추가
				}else if(dlCd == 'PK030'){
					displayForMatter = 'scwin.statusDisFormatter'; //데이터유형이 장치 상태 경우 displayformaatter 추가
				}else if(dlCd == 'PK039'){
					displayForMatter = 'scwin.fcltContModeFormatter'; //데이터유형이 신호제어 모드인 경우 displayformaatter 추가
				}

				if(paramKind != '' && fcltKind[x] == fcltKindParam){
					if(dlCd == "PK046"){
						gridBody +='<w2:column removeBorderStyle="false" width="113" inputType="button" style="height:20px" id="'+paramKind+'" value="보기" blockSelect="false" displayMode="label" displayFormatter="'+displayForMatter+'"></w2:column>';
					}else{
						gridBody +='<w2:column removeBorderStyle="false" width="113" inputType="text" style="height:20px" id="'+paramKind+'" value="" blockSelect="false" displayMode="label" displayFormatter="'+displayForMatter+'"></w2:column>';
					}
					//gridBody +='<w2:column removeBorderStyle="false" width="113" inputType="text" style="height:20px" id="'+paramKind+'_AUTO_YN'+'" value="" blockSelect="false" displayMode="label" displayFormatter="scwin.AutoDisFormatter"></w2:column>';
				}
			}

			//gridView 정보 정의 
			//xml 형식을 정의 하면 <w2:gridView 에 xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" 포함 합니다.
			var gridStr = '<w2:gridView xmlns:w2="http://www.inswave.com/websquare" xmlns:ev="http://www.w3.org/2001/xml-events" '
								+'autoFit="lastColumn" class="cm_grid left" dataList="data:'+dataList+'" ev:oncellclick="scwin.'+gridId+'_oncellclick" '
								+'ev:onrowindexchange="scwin.'+gridId+'_onrowindexchange" fixedColumnWithHidden="true" id="'+gridId+'" ignoreCellClick="false" rowNumHeaderValue="No." rowNumVisible="true" '
								+'rowStatusVisible="false" rowStatusWidth="40" scrollByColumn="false" style="" useShiftKey="true" autoFitMinWidth="0" readOnly="true" '
								+'sortable="false" focusMode="row" showSortableImage="true" sortEvent="onclick" ev:onheaderclick="scwin.MO0205G2_onheaderclick">';
			gridStr += '<w2:header style="" id="header2">'
			+ '<w2:row style="" id="row3">'
			+ gridHeader
			//+'<w2:column removeBorderStyle="false" width="100" inputType="text" style="height:20px" id="h_colunm_5" value="" blockSelect="false" displayMode="label"></w2:column>'
			+'</w2:row>'
			+'</w2:header>'
			+'<w2:gBody style="" id="gBody2">'
			+'<w2:row id="row2" style="">'
			+ gridBody
			//+'<w2:column removeBorderStyle="false" width="117" inputType="text" style="height:20px" id="" value="" blockSelect="false" displayMode="label"></w2:column>'
			+'</w2:row>'
			+'</w2:gBody>';
			gridStr += '<w2:footer id="footer1" style=""><w2:row id="row3" style=""><w2:column blockSelect="false" colSpan="30" displayMode="label" expression="&quot;데이터 개수 : &quot; + COUNT(\'COMP_ID\')" id="column42" inputType="expression" removeBorderStyle="false" style="height:20px;" textAlign="left" width="2386"></w2:column></w2:row></w2:footer>';
			gridStr += '</w2:gridView>';


			switch (fcltKind[x]) {
			case "FK005":
				MO0205G2.setGridStyle(WebSquare.xml.parse(gridStr , true ));
				break;
			case "FK007":
				MO0205G3.setGridStyle(WebSquare.xml.parse(gridStr , true ));
				break;
			case "FK004":
				MO0205G4.setGridStyle(WebSquare.xml.parse(gridStr , true ));
				break;
			case "FK006":
				MO0205G5.setGridStyle(WebSquare.xml.parse(gridStr , true ));
				break;
			}

			//dlt_BMS_FCLT_COND_PARAM_INFO.removeAll();
			//autoOptions.Sub1.grid = MO0205G2;
			scwin.commonCode();

		}


		//이미지 생성 함수
		scwin.makeImage();

	};

	//이미지 생성 함수
	scwin.makeImage = function(){
		var str = ""
		var imgSrc = ""
		var imgId = ""

		//var param = com.getParameter();

		//시설물상태에 따라 클래스(백그라운드 색) 설정
		for (i=0; i<dlt_BMS_FCLT_INFO.getDataLength(); i++) {
			//비정상
			if (dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COND") == "FC021") {
				MO0205G2.setCellClass(i, "FCLT_COND", "cond_abnormal");
			}
			//정상
			else if (dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COND") == "FC020") {
				MO0205G2.setCellClass(i, "FCLT_COND", "cond_normal");
			}
		}
		for (i=0; i<dlt_BMS_FCLT_INFO.getDataLength(); i++) {
			var coord = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COORDS").split(',');
			var top = coord[0];
			var left = coord[1];
			var fcltKind = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_KIND");
			var fcltId = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_ID");
			var fcltCond = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COND");

			if(fcltKind == 'FK001' && (fcltCond == 'FC020'|| fcltCond == '')){
				imgSrc = "/cm/images/bms/BIT_RED_normal.png";
			}else if(fcltKind == 'FK001' && (fcltCond == 'FC021' || fcltCond == '')){
				imgSrc = "/cm/images/bms/BIT_RED_abnormal.png";
			}else if(fcltKind == 'FK004' && (fcltCond == 'FC020' || fcltCond == '')){
				imgSrc = "/cm/images/bms/AIRTUBE_normal.png";
			}else if(fcltKind == 'FK004' && (fcltCond == 'FC021' || fcltCond == '')){
				imgSrc = "/cm/images/bms/AIRTUBE_abnormal.png";
			}else if(fcltKind == 'FK005' && (fcltCond == 'FC020' || fcltCond == '')){
				imgSrc = "/cm/images/bms/AIRCON_normal.png";
			}else if(fcltKind == 'FK005' && (fcltCond == 'FC021' || fcltCond == '')){
				imgSrc = "/cm/images/bms/AIRCON_abnormal.png";
			}else if(fcltKind == 'FK007' && (fcltCond == 'FC020' || fcltCond == '')){
				imgSrc = "/cm/images/bms/SCREENDOOR_normal.png";
			}else if(fcltKind == 'FK006' && (fcltCond == 'FC020' || fcltCond == '')){
				imgSrc = "/cm/images/bms/PEOPLECOUNT_normal.png";
			}

			//시설물 이미지
			str += '<div id="'+fcltId+'" style="z-index: 1; position: absolute; top:'+top+'px; left: '+left+'px;" class="w2group fclt_img" onclick="imgOnclick(this.id);">';
			str += '<img xmlns="http://www.w3.org/1999/xhtml" style="" src="'+imgSrc+'" id="'+fcltId+'img'+'">';
			str += '</div>'

		}

		//정류소 이미지
		if(dlt_BMS_NODE_MST.getCellData(MO0205G0.getFocusedRowIndex(), "STTN_FCLT_TYPE") == 'ST003'){
			var imgSrc = "/cm/images/bms/ST003_1.png"
		}else{
			var imgSrc = "/cm/images/bms/ST002_2.jpg"
		}

		var nodeId = dlt_BMS_NODE_MST.getCellData(MO0205G0.getFocusedRowIndex(), "NODE_ID");

		str += '<img xmlns="http://www.w3.org/1999/xhtml" style="position:absolute; cursor:default" src="'+imgSrc+'" id="'+nodeId+'img'+'">';

		$('#'+STTN_IMG.getID()).html(str);

		var fclt_ctrindex = MO0205G2.getColumnIndex("FCLT_CTR");
		//var param = com.getParameter();

		for(var i=0; i<dlt_BMS_FCLT_INFO.getDataLength(); i++){
			var ctr_yn = dlt_BMS_FCLT_INFO.getCellData(i, "CTR_YN");
			if(ctr_yn == 'N'){

				//MO0205G2.setDisabled("cell", i, "FCLT_CTR", true);
	            //제어 버튼 안보이게 처리
	            MO0205G2.setCellClass(i, "FCLT_CTR", "hideData");
			}
		}

	}

	scwin.commonCode = function(){
		var codeOptions = [ 
							{code : "FCLT_KIND", compID : "MO0205G2:FCLT_KIND"}
							,{code : "FCLT_COND", compID : "MO0205G2:FCLT_COND"}
		 ];			

		com.setCommonDtl(codeOptions);
	}

	//이미지 클릭 이벤트
	function imgOnclick(id){
		var rowIndex = dlt_BMS_FCLT_INFO.getMatchedIndex("FCLT_ID", id);
		var fcltKind = dlt_BMS_FCLT_INFO.getCellData(rowIndex, "FCLT_KIND");
		var fcltId = dlt_BMS_FCLT_INFO.getCellData(rowIndex, "FCLT_ID");

		switch (fcltKind) {
		case "FK005":
			var index = dlt_BMS_AIRCON_FCLT_INFO.getMatchedIndex("FCLT_ID", fcltId);
			MO0205G2.setFocusedCell(index, "FCLT_ID", false);
			MO0205G3.removeFocusedCell();
			MO0205G4.removeFocusedCell();
			MO0205G5.removeFocusedCell();
			break;

		case "FK007": 
			var index = dlt_BMS_SCRDOOR_FCLT_INFO.getMatchedIndex("FCLT_ID", fcltId);
			MO0205G3.setFocusedCell(index, "FCLT_ID", false);
			MO0205G2.removeFocusedCell();
			MO0205G4.removeFocusedCell();
			MO0205G5.removeFocusedCell();
			break;

		case "FK004":
			var index = dlt_BMS_AIRTUBE_FCLT_INFO.getMatchedIndex("FCLT_ID", fcltId);
			MO0205G4.setFocusedCell(index, "FCLT_ID", false);
			MO0205G2.removeFocusedCell();
			MO0205G3.removeFocusedCell();
			MO0205G5.removeFocusedCell();
			break;

		case "FK006":
			var index = dlt_BMS_PLECNT_FCLT_INFO.getMatchedIndex("FCLT_ID", fcltId);
			MO0205G5.setFocusedCell(index, "FCLT_ID", false);
			MO0205G2.removeFocusedCell();
			MO0205G3.removeFocusedCell();
			MO0205G4.removeFocusedCell();
			break;

		}


	}

	scwin.MO0205G2_onrowindexchange = function(row,oldRow) {
		MO0205G3.removeFocusedCell();
		MO0205G4.removeFocusedCell();
		MO0205G5.removeFocusedCell();

		var str = ""
		var imgSrc = ""
		var imgId = ""

		for (i=0; i<dlt_BMS_FCLT_INFO.getDataLength(); i++) {
			var coord = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_COORDS").split(',');
			var top = coord[0];
			var left = coord[1];
			var fcltKind = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_KIND");
			var fcltId = dlt_BMS_FCLT_INFO.getCellData(i, "FCLT_ID");
			var fcltCond = dlt_BMS_FCLT_INFO.getCellData(i, "PK030");

			if(fcltKind == 'FK005' && (fcltCond == '1' || fcltCond == '')){
				imgSrc = "/cm/images/bms/AIRCON_normal.png";
			}else if(fcltKind == 'FK005' && (fcltCond == '0' || fcltCond == '')){
				imgSrc = "/cm/images/bms/AIRCON_abnormal.png";
			}else if(fcltKind == 'FK007' && (fcltCond == '1' || fcltCond == '')){
				imgSrc = "/cm/images/bms/SCREENDOOR_normal.png";
			}else if(fcltKind == 'FK004' && (fcltCond == '1' || fcltCond == '')){
				imgSrc = "/cm/images/bms/AIRTUBE_normal.png";
			}else if(fcltKind == 'FK004' && (fcltCond == '0' || fcltCond == '')){
				imgSrc = "/cm/images/bms/AIRTUBE_abnormal.png";
			}else if(fcltKind == 'FK006' && (fcltCond == '1' || fcltCond == '')){
				imgSrc = "/cm/images/bms/PEOPLECOUNT_normal.png";
			}

			str += '<div id="'+fcltId+'"style="z-index: 1;position: absolute; top:'+top+'px; left: '+left+'px;" class="w2group fclt_img" onclick="imgOnclick(this.id);">';
			str += '<img xmlns="http://www.w3.org/1999/xhtml" style="" src="'+imgSrc+'" id="'+fcltId+'img'+'">';
			str += '</div>'

		}

		//정류소 이미지
		if(dlt_BMS_NODE_MST.getCellData(MO0205G0.getFocusedRowIndex(), "STTN_FCLT_TYPE") == 'ST003'){
			var imgSrc = "/cm/images/bms/ST003_1.png"
		}else{
			var imgSrc = "/cm/images/bms/ST002_2.jpg"
		}

		var nodeId = dlt_BMS_NODE_MST.getCellData(MO0205G0.getFocusedRowIndex(), "NODE_ID");

		str += '<img xmlns="http://www.w3.org/1999/xhtml" style="position:absolute; cursor:default" src="'+imgSrc+'" id="'+nodeId+'img'+'">';

		$('#'+STTN_IMG.getID()).html(str);

		var fcltID = dlt_BMS_AIRCON_FCLT_INFO.getCellData(row, "FCLT_ID");
		var fcltKind = dlt_BMS_AIRCON_FCLT_INFO.getCellData(row, "FCLT_KIND");
		var fcltCond = dlt_BMS_AIRCON_FCLT_INFO.getCellData(row, "PK030");

		//이미지 변경
		if(fcltKind == 'FK005' && (fcltCond == '1'|| fcltCond == '')){
			$('#'+fcltID).children('img').attr("src", '/cm/images/bms/AIRCON_normal_selected.png');
		}else if(fcltKind == 'FK005' && (fcltCond == '0'|| fcltCond == '')){
			$('#'+fcltID).children('img').attr("src", '/cm/images/bms/AIRCON_abnormal_selected.png');
		}

		oriBgWidth = document.getElementById(STTN_IMG.getID()).offsetWidth;
		oriBgHeight = document.getElementById(STTN_IMG.getID()).offsetHeight;
	};

