	//오픈메뉴
	scwin.openMenu = function(menuNm, url, menuCode, paramObj, menuType) {
		// client에서 url 숨기기 메뉴일 경우에는 새 창으로 띄우기 적용 
		if (url == "/") {
			var url = document.location.href + "/";
			window.open(url, "", "width=1200, height=700, left=450, top=100");
		} else {
			menuCode = menuCode || "";
			var layout = $p.top().scwin.getLayoutId();
			var tmpUrl;
			var menuCodeParm = menuCode;
			var frameMode;	// "wframe", "iframe"
			var favStatus;
			var data;

			if (url.indexOf("/") !== 0) {
				url = "/" + url;
			}
			url = gcm.CONTEXT_PATH + url;
			if ((typeof paramObj !== "undefined") && (paramObj !== null)) {
				data = {};
				data.paramObj = paramObj;
			} else {
				data = {};
			}

			data.menuNm = menuNm;
			data.menuCode = menuCode;
			data.favStatus = favStatus;
			data.menuType = menuType;

			var _closable = true;

			var frameMode = "";
			if (layout == "T") {
				var tabObj = { closable : _closable, //main 페이지를 제외하고 탭 닫기 기능 제공
							   openAction : "select", // exist 는 기존 탭을 갱신, new 는 항상 새로, select는 동일 id 가 존재하면 선택, last: 기존 tab을 마지막 tab으로 이동후 선택
							   label : menuNm };

				if (com.getFrameMode()  === "wframe") {
					frameMode = "wframePreload";
				} else {
					frameMode = "iframe";
				}

				var contObj = {
					frameMode : frameMode,
					scope : true,
					src : url,
					alwaysDraw : false,
					title : menuNm,
					dataObject : {
						type : "json", 
						name : "param", 
						data : data
					}
				};
				var tabComp = $w.getComponentById("mf_tac_layout");
				tabComp.addTab(menuCode, tabObj, contObj);

				// tabObj의 openAction의 last값의 동작 특이 사항으로 선택이 되지 않은 경우 선택하는 로직 추가
				if (tabComp.getSelectedTabID() !== menuCode) {
					var tabIndex = tabComp.getTabIndex(menuCode);
					if (tabIndex) {
						tabComp.setSelectedTabIndex(tabIndex);
					}
				}
			} else if (layout == "M") {
				if (com.getFrameMode() === "wframe") {
					frameMode = "wframe";
				} else {
					frameMode = "iframe";
				}

				var options = {
					title : menuNm,
					src : url,
					windowTitle : menuNm,
					windowId : menuCode,
					openAction : "existWindow",
					frameMode : frameMode,
					dataObject : { 
						type : "json", 
						name : "param", 
						data : data
					}
				}
				var wdcComp = $w.getComponentById("mf_wdc_main");
				wdcComp.createWindow(options);
			}
		}
	};

	scwin.moveCtrlClick = function() {
		var fcltId = dlt_BMS_FCLT_INFO.getCellData(MO0205G2.getFocusedRowIndex(), "FCLT_ID");

		if(fcltId == null || fcltId == ''){
			com.alert("시설물을 선택해주세요.");
		}else{
			scwin.openMenu("시설물 상태 및 제어","/ui/BMS/FM0204/FM0204M01.xml", "MN00040402",fcltId);
			var bac= null;		
		}
	};

	//데이터유형이 ON/OFF인 경우 disformatter 추가 함수
	scwin.onOffDisFormatter = function(data){
		if(data == ""){
			return "";
		}else if(data == '1'){
			return "ON";
		}else if(data == '0'){
			return "OFF";
		}else if(data == 'on'){
			return "ON";
		}else if(data == 'off'){
			return "OFF";
		}else{
			return data;
		}
	}

	//데이터유형이 장치상태인 경우 disformatter 추가 함수
	scwin.statusDisFormatter = function(data){
		if(data == ""){
			return "";
		}else if(data == '0'){
			return "통신이상";
		}else if(data == '1'){
			return "정상";
		}else{
			return data;
		}
	}

	//데이터유형이 문 열림/닫힘인 경우 disformatter 추가 함수
	scwin.openCloseDisFormatter = function(data){
		if(data == ""){
			return "";
		}else if(data == '1'){
			return "열림";
		}else if(data == '0'){
			return "닫힘";
		}else{
			return data;
		}
	}

	//데이터유형이 자동여부인 경우 disformatter 추가 함수
	scwin.AutoDisFormatter = function(data){
		if(data == ""){
			return "";
		}else if(data == 'Y'){
			return "예";
		}else if(data == 'N'){
			return "아니요";
		}
	}

	//신호제어기 장치상태 disformatter 함수
	scwin.fcltCondFormatter = function(data){
		if(data == ""){
			return "";
		}else if(data == '0'){
			return "통신이상";
		}else if(data == '1'){
			return "정상";
		}else{
			return data;
		}
	}

	//신호제어기 신호제어 모드 disformatter 함수
	scwin.fcltContModeFormatter = function(data){
		if(data == ""){
			return "";
		}else if(data == '0'){
			return "오프라인";
		}else if(data == '2'){
			return "온라인";
		}else if(data == '3'){
			return "전이";
		}else{
			return data;
		}
	}

