					 COMP_ID : dlt_BRT_INCDNT_INFO.getCellData(row, "COMP_ID"), 
					 ROW_INDEX : row,
					 callbackFn : "scwin.btn_position_callback"}

		var options = {
			id : "MO0303P01",
			popupName : "위치 설정",
			modal : true,
			width : 900,
			height : 590
		};
		com.openPopup(("/ui/BRT/MO0303/MO0303P01.xml"), options, data);
	};

	scwin.btn_position_callback	= function(obj) {
