head.script:

scwin.onpageload = function() {
	scwin.param = com.getParameter();

	dma_search.set("REP_ROUT_ID", scwin.param.incdnt_rep_rout_id);
	messageText.setValue(scwin.param.incdnt_res_remark);

	scwin.closeCallbackFncName = scwin.param.callbackFn;
	com.setEnterKeyEvent(popup_header, scwin.btn_search_onclick);
	var btnOptions = 
	[
		{userStr:"조회", auth: gcm.BTN.SEARCH, cbFnc: scwin.btn_search_onclick}
	];
	com.setMainBtn3(wfm_mainBtn, btnOptions);

	var codeOptions = [{ code : "AREA", compID : "MO0303P0:AREA"}
					];

	dma_search.set("REP_ROUT_ID", scwin.param.data);
	com.setCommonDtl(codeOptions, function() {
		com.searchGrid(MO0303P1, sub_MO0303P3R1);			
		//com.executeSubmission(sub_allocVhcList);
	});

	//com.executeSubmission(sub_vhcList);			
};

scwin.onpageunload = function() {

};

scwin.btn_search_onclick = function() {
	com.executeSubmission(sub_vhcList);
};

scwin.btn_confirm_onclick = function() {
	var jsonData = MO0303P1.getCheckedJSON("chk");
	var vhcArray = new Array();

	for (i=0; i<jsonData.length; i++) {
		vhcArray[i] = jsonData[i].VHC_ID;	
	}

	var sendMessage = messageText.getValue(); 


	if(sendMessage != "") {
		dma_search.set("VHC_IDS", vhcArray);
		dma_search.set("MSG_CONTS", sendMessage);
		com.executeSubmission(sub_MO0303SCK1);
		com.closePopup();
	}		

	else {
		com.alert("돌발 메시지 내용을 입력해 주세요.");	
	}

};

scwin.btn_cancel_onclick = function() {
	com.closePopup();
};

scwin.vhcListGrid_onrowindexchange = function(row,oldRow) {
	vhcListGrid.setCellChecked(row,"chk",true);
};

scwin.sbx_ChoiceBox_onviewchange = function(info) {		

	if (info.newValue == "ALL") {
		acb_autoChoiceBox.hide();
		search_input.setValue("");
		search_input.show("");
	} 
	else if (info.newValue == "VHC_NO") {
		search_input.hide();
		acb_autoChoiceBox.setNodeSet("data:dlt_vhcItem", "VHC_NO", "VHC_NO");
		acb_autoChoiceBox.show("");
	}
	else if (info.newValue == "VHC_ID") {
		search_input.hide();
		acb_autoChoiceBox.setNodeSet("data:dlt_vhcItem", "VHC_ID", "VHC_ID");
		acb_autoChoiceBox.show("");
	}		
};

scwin.sub_vhcList_submitdone = function(e) {
	com.excludeItemsByGrid(MO0303P0, dma_VHC_IDS.get("VHC_IDS"), "VHC_ID");
};

scwin.NullData_onviewchange = function(info) {

	if(info.checked == true){
		com.executeSubmission(sub_vhcList);
		vhcListGrid.setDisabled("grid", true);
		vhcListGrid.setDisabled("column","chk",true);
	}
	else{
		vhcListGrid.setDisabled("grid", false);
		vhcListGrid.setDisabled("column","chk",false);
	}

};

// -> 버튼
scwin.btn_grid_add_onclick = function() {
	var rowIndexArr = MO0303P0.getCheckedIndex("chk");
	var checkedJson = MO0303P0.getCheckedJSON("chk");

	for(i=0; i<checkedJson.length; i++) {
		var insertIndex = dlt_BRT_CUR_OPER_INFO.insertRow();			
		dlt_BRT_CUR_OPER_INFO.setCellData(insertIndex, "VHC_ID", checkedJson[i].VHC_ID);
		dlt_BRT_CUR_OPER_INFO.setCellData(insertIndex, "VHC_NO", checkedJson[i].VHC_NO);					
	}
	dlt_BMS_VHC_MST.removeRows(rowIndexArr);
};

// <- 버튼
scwin.btn_grid_del_onclick = function() {

	var rowIndexArr = MO0303P1.getCheckedIndex("chk");
	var checkedJson = MO0303P1.getCheckedJSON("chk");

	for(i=0; i<checkedJson.length; i++) {
		var insertIndex = dlt_BMS_VHC_MST.insertRow();			
		dlt_BMS_VHC_MST.setCellData(insertIndex, "VHC_ID", checkedJson[i].VHC_ID);
		dlt_BMS_VHC_MST.setCellData(insertIndex, "VHC_NO", checkedJson[i].VHC_NO);					
	}
	dlt_BRT_CUR_OPER_INFO.removeRows(rowIndexArr);

	dlt_BMS_VHC_MST.sort("VHC_ID", 0);
};	

scwin.sub_MO0303P3R1_submitdone = function(e) {
	var incdnt_gps_x = scwin.param.incdnt_gps_x;
	var incdnt_gps_y = scwin.param.incdnt_gps_y;
	var circleLatLng = new kakao.maps.LatLng(scwin.param.incdnt_gps_y, scwin.param.incdnt_gps_x);

	var circle = new kakao.maps.Circle({
	    center : circleLatLng,  // 원의 중심좌표 입니다 
	    radius: 3000 // 미터 단위의 원의 반지름입니다 
	}); 		

	for (i=0; i<dlt_BRT_CUR_OPER_INFO.getDataLength(); i++){
		var car_gps_x = dlt_BRT_CUR_OPER_INFO.getCellData(i, "GPS_X");
		var car_gps_y = dlt_BRT_CUR_OPER_INFO.getCellData(i, "GPS_Y");
		var carLatLng = new kakao.maps.LatLng(car_gps_y, car_gps_x);

		//돌발 위치와 차 위치를 경로로 하는 폴리라인 설정
		var line = new kakao.maps.Polyline();
		var path = [carLatLng, circleLatLng];
		line.setPath(path);

		//차 좌표와 돌발 중심 사이의 거리
		var dist = line.getLength();

		//이 거리가 원의 반지름보다 크다면 차는 돌발 알림 범위밖에 있음
		if (dist > circle.getRadius()) {
			dlt_BRT_CUR_OPER_INFO.removeRow(i);
		}
	}

	MO0303P1.checkAll("chk", 1);

	//com.searchGrid(MO0303P0, sub_vhcList);
	com.executeSubmission(sub_allocVhcList);

};

scwin.sub_allocVhcList_submitdone = function(e) {

	var incdnt_gps_x = scwin.param.incdnt_gps_x;
	var incdnt_gps_y = scwin.param.incdnt_gps_y;
	var incdnt_rep_rout_id = scwin.param.incdnt_rep_rout_id;
	var circleLatLng = new kakao.maps.LatLng(scwin.param.incdnt_gps_y, scwin.param.incdnt_gps_x);	

	for(i=0; i<dlt_BRT_CUR_ALLOC_PL_INFO.getDataLength(); i++) {

		if(dlt_BRT_CUR_ALLOC_PL_INFO.getCellData(i, "REP_ROUT_ID") == incdnt_rep_rout_id) {
			var insertIndex = dlt_BRT_CUR_OPER_INFO.insertRow();
			dlt_BRT_CUR_OPER_INFO.setCellData(insertIndex, "REP_ROUT_ID", incdnt_rep_rout_id);
			dlt_BRT_CUR_OPER_INFO.setCellData(insertIndex, "VHC_ID", dlt_BRT_CUR_ALLOC_PL_INFO.getCellData(i, "VHC_ID"));
			dlt_BRT_CUR_OPER_INFO.setCellData(insertIndex, "VHC_NO", dlt_BRT_CUR_ALLOC_PL_INFO.getCellData(i, "VHC_NO"));
			dlt_BRT_CUR_OPER_INFO.setCellData(insertIndex, "COMP_NM", dlt_BRT_CUR_ALLOC_PL_INFO.getCellData(i, "COMP_NM"));
		}
	}

	//컬럼 dstinct된 데이터들
	var distinct_VHC_IDS = dlt_BRT_CUR_OPER_INFO.getColumnDistinctDataArray("VHC_ID");
	var distinct_VHC_NOS = dlt_BRT_CUR_OPER_INFO.getColumnDistinctDataArray("VHC_NO");

	dlt_BRT_CUR_OPER_INFO.removeAll();

	for (i=0; i<distinct_VHC_IDS.length; i++) {
		var insertIndex = dlt_BRT_CUR_OPER_INFO.insertRow();			
		dlt_BRT_CUR_OPER_INFO.setCellData(insertIndex, "VHC_ID", distinct_VHC_IDS[i]);
		dlt_BRT_CUR_OPER_INFO.setCellData(insertIndex, "VHC_NO", distinct_VHC_NOS[i]);			
	}
	MO0303P1.checkAll("chk", 1);

	var val_VHC_IDS = dlt_BRT_CUR_OPER_INFO.getColData("VHC_ID");
	dma_VHC_IDS.set("VHC_IDS", val_VHC_IDS);

	//com.excludeItemsByGrid(MO0303P1, dma_VHC_IDS.get("VHC_IDS"), "VHC_ID");

	//var val_GRG_ID = dlt_BMS_GRG_MST.getColData("GRG_ID");
	//com.excludeItemsByGrid(SI0102P0, ids, "GRG_ID");
	//dma_param_COMPID.set("GRG_IDS", val_GRG_ID);
	com.searchGrid(MO0303P0, sub_vhcList);
};

scwin.MO0303P0_oncellclick = function(row,col,colId) {
	if(col==0)return;
	if(MO0303P0.getCellChecked(row,"chk")){
		MO0303P0.setCellChecked(row,"chk",false)
	}
	else {
		MO0303P0.setCellChecked(row,"chk",true)
	}		
};

scwin.MO0303P1_oncellclick = function(row,col,colId) {
	if(col==0)return;
	if(MO0303P1.getCellChecked(row,"chk")){
		MO0303P1.setCellChecked(row,"chk",false)
	}
	else {
		MO0303P1.setCellChecked(row,"chk",true)
	}			
};
