scwin.ST0402G0_oncellclick = function(row,col,colId) {
	if (col != "0"){
	   if(ST0402G0.getCellChecked(row, 'chk')==0){
	      ST0402G0.setCellChecked(row, 'chk', true);
	   }else if(ST0402G0.getCellChecked(row, 'chk')==1){
	      ST0402G0.setCellChecked(row, 'chk', false);
	   }
	}

	var chkJsonData = ST0402G0.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var routId = [];

	//체크 가져오기
	for(i; i<chkCnt; i++){
		routId.push(chkJsonData[i].ROUT_ID);
	}

	dma_sub_search.set("ROUT_ID", routId);
	com.executeSubmission(sub_ST0402G1R0);
};

