scwin.getRandomColor = function(_isAlpha) {
	  var r = getRand(0, 255),
	  g = getRand(0, 255),
	  b = getRand(0, 255),
	  a = getRand(0, 10) / 10;

	  var rgb = _isAlpha ? 'rgba' : 'rgb';
	  rgb += '(' + r + ',' + g + ',' + b;
	  rgb += _isAlpha ? ',' + a + ')' : ')';

	  return rgb;

	  // Return random number from in to max
	  function getRand(min, max) {
	    if (min >= max) return false;
	    return ~~(Math.random() * (max - min + 1)) + min;
	  };
	};


var CharData = function(){
	this.seriesname = {};
	this.fill = {};
	this.backgroundColor = {};
	this.borderColor = {};
	this.data = [];
}


scwin.drawChart = function(chkCnt, chkJsonDataArr){
	var datasets = new Array();
	var transData = new Array();
	var i = 0;
	var j = 0;
	var num = 0;

	var rowCount = dlt_BRT_OPER_VIOLT_STAT2.getRowCount();
	var jsonData = dlt_BRT_OPER_VIOLT_STAT2.getAllJSON();

	for(i; i<chkCnt; i++){
		var data = new Array();
		var xLabel = new Array();
		var color = new Array();
		var charData = new CharData();

		var rgb = scwin.getRandomColor();
		charData.label = chkJsonDataArr[i].ROUT_NM;
		charData.borderColor = rgb;
		charData.backgroundColor = rgb;

		for(j; j<rowCount; j++){
			if(jsonData[num].ROUT_ID ==  dlt_BRT_OPER_VIOLT_STAT2.getCellData(j, "ROUT_ID")){
				transData.push(dlt_BRT_OPER_VIOLT_STAT2.getRowJSON(j));
			}else if(jsonData[num].ROUT_ID  !=  dlt_BRT_OPER_VIOLT_STAT2.getCellData(j, "ROUT_ID")){
				num = j;
				j = j;
				break;
			}
		}

		for(var y = 0; y < transData.length; y++){
			var label = transData[y].ROUT_NM;
			var sumCnt = transData[y].SUM_CNT;
			var incdntType = transData[y].VIOLT_TYPE;

			var showType = "";
			switch(incdntType){
				case "VL001":
					showType = "급가속";
					break;
				case "VL002":
					showType = "급감속";
					break;
				case "VL003":
					showType = "과속";
					break;
				case "VL004":
					showType = "무정차통과";
					break;					
				case "VL005":
					showType = "개문주행";
					break;					
				case "VL006":
					showType = "급출발";
					break;					
				case "VL007":
					showType = "노선이탈";
					break;					
				case "VL008":
					showType = "급정지";
					break;					

			}				

			var categoryJson = new Object();
			var labelJson = new Object();

			categoryJson = sumCnt;
			labelJson = showType;

			xLabel.push(labelJson);
			data.push(categoryJson);

		};


			charData.data = data;
			datasets.push(charData);			

	transData = [];
} //end for

	var ctx = document.getElementById('chart_ST0402');

	if(chart_ST0402 != null) {
		chart_ST0402.destroy();	
	}

	chart_ST0402 = new Chart(ctx, {
		    type: 'bar',
			data: {
				labels:xLabel,
				datasets
			  },
			options: {
				maintainAspectRatio: false,
				scales: {
					yAxes: [{
							ticks: {
								beginAtZero: true,
								userCallback: function(label) {
									if(Math.floor(label) === label) {
									return label;	
									}	
								}	
							}
					}]	
				}	

			}  //end options				  

		});

}


