scwin.sbx_ChoiceBox_onviewchange = function(info) {		
	if (info.newValue == "ALL") {
		autoChoiceBox.hide();
		search_input.setValue("");
		search_input.show("");
	} else if (info.newValue == "REP_ROUT_NM") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_searchitem", "REP_ROUT_NM", "REP_ROUT_NM");
		autoChoiceBox.show("");
	} else if (info.newValue == "ROUT_NM") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_searchitem", "ROUT_NM", "ROUT_NM");
		autoChoiceBox.show("");
	} 
};	

scwin.ST0402G1_onrefresh = function(options) {
	var incdntLength = $(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel").length;
	for (var i=0; i<incdntLength; i++) {
		var incdntType = $(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel")[i].innerHTML;
		switch(incdntType.trim()) {
			case "VL001":
				showType = "급가속";
				break;
			case "VL002":
				showType = "급감속";
				break;
			case "VL003":
				showType = "과속";
				break;
			case "VL004":
				showType = "무정차통과";
				break;					
			case "VL005":
				showType = "개문주행";
				break;					
			case "VL006":
				showType = "급출발";
				break;					
			case "VL007":
				showType = "노선이탈";
				break;					
			case "VL008":
				showType = "급정지";
				break;					

		}
		$(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel")[i].innerHTML = showType;				
	}

};



