	scwin.drawChart = function(vhcNo){

		var datasets = new Array();
		var transData = new Array();
		var i = 0;
		var j = 0;
		var num = 0;

		var rowCount = dlt_BRT_DVC_STAT.getRowCount();
		var jsonData = dlt_BRT_DVC_STAT.getAllJSON();

		for(i; i<chkCnt; i++){
			var data = new Array();
			var xLabel = new Array();
			var color = new Array();
			var charData = new CharData();

			var rgb = scwin.getRandomColor();
			charData.label = vhcNo;
			charData.borderColor = rgb;
			charData.backgroundColor = rgb;

			for(j; j<rowCount; j++){
				if(jsonData[num].VHC_ID ==  dlt_BRT_DVC_STAT.getCellData(j, "VHC_ID")){
					transData.push(dlt_BRT_DVC_STAT.getRowJSON(j));
				}else if(jsonData[num].VHC_ID  !=  dlt_BRT_DVC_STAT.getCellData(j, "VHC_ID")){
					num = j;
					j = j;
					break;
				}
			}

			for(var y = 0; y < transData.length; y++){

				var label = transData[y].VHC_NO;
				var sumCnt = transData[y].SUM_CNT;
				var incdntType = transData[y].DVC_KIND;
				//var workAmt = transData[y].WORK_AMT; 
				//var aplyDt = jsonData[y].APLY_DATE;


				var showType = "";
				switch(incdntType){		
					case "DK001":
						showType = "OBE";
						break;
					case "DK002":
						showType = "운전자단말기";
						break;	
					case "DK003":
						showType = "승객용안내기";
						break;	
					case "DK004":
						showType = "전자노선도";
						break;	
					case "DK005":
						showType = "행선지키패드";
						break;		
					case "DK006":
						showType = "행선지안내기X정면 12열(싸인)";
						break;	
					case "DK007":
						showType = "행선지안내기X정면 11열(애드앤텍)";
						break;	
					case "DK008":
						showType = "행선지안내기X측면(싸인)";
						break;
					case "DK009":
						showType = "행선지안내기X측면2(싸인)";
						break;	
					case "DK010":
						showType = "투명디스플레이";
						break;	
					case "DK011":
						showType = "태그리스";
						break;
					case "DK012":
						showType = "행선지안내기X측면(애드앤텍)";
						break;
					case "DK013":
						showType = "태그리스";
						break;	
				}	

				var categoryJson = new Object();
				var labelJson = new Object();


				categoryJson = sumCnt;

				labelJson = showType;

				xLabel.push(labelJson);
				data.push(categoryJson);
			};

				//charData.label = label;
				//charData.borderColor = rgb;
				//charData.backgroundColor = rgb;
				//charData.borderWidth = 1;
				//charData.pointBorderWidth = 1;
				//charData.tension = 0;
				//charData.pointRadius = 1;


				charData.data = data;
				datasets.push(charData);			

		transData = [];
	} //end for

		var ctx = document.getElementById('chart_ST0501');

		if(chart_ST0501 != null) {
			chart_ST0501.destroy();	
		}

		chart_ST0501 = new Chart(ctx, {
			    type: 'bar',
				data: {
					labels:xLabel,
					datasets
				  },
				options: {
					maintainAspectRatio: false,
					scales: {
				      yAxes: [{
                            display: true,
                            ticks: {
                                beginAtZero: true,
                                steps: 10,
                                stepValue: 0,
                                max: 80
                            }
                        }]
			        }
				}  //end options				  

			});

	}

