	scwin.sbx_ChoiceBox_onviewchange = function(info) {		
		if (info.newValue == "ALL") {
			autoChoiceBox.hide();
			search_input.setValue("");
			search_input.show("");
		} else if (info.newValue == "REP_VHC_NM") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_searchitem", "REP_VHC_NM", "REP_VHC_NM");
			autoChoiceBox.show("");
		} else if (info.newValue == "VHC_NM") {
			search_input.hide();
			autoChoiceBox.setNodeSet("data:dlt_searchitem", "VHC_NM", "VHC_NM");
			autoChoiceBox.show("");
		} 
	};	

	scwin.ST0501G1_onrefresh = function(options) {
		var incdntLength = $(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel").length;
		var dvcLength = $(".w2editablePivot_contentTable_content_TR").find(".pvtRowLabel").length;

		for (var i=0; i<incdntLength; i++) {
			var incdntType = $(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel")[i].innerHTML;
			switch(incdntType.trim()) {			
					case "WT001":
						showType = "최초설치";
						break;
					case "WT002":
						showType = "장치수리";
						break;	
					case "WT003":
						showType = "교체설치";
						break;	
					case "WT004":
						showType = "정기정검";
						break;	
					case "WT005":
						showType = "폐기";
						break;				
			}
			$(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel")[i].innerHTML = showType;				
		}
		for (var i=0; i<dvcLength; i++) {
			var incdntType2 = dlt_BRT_DVC_STAT.getCellData(i, "DVC_KIND");
