scwin.onpageload = function() {
	var autoOptions = {
		Main : { grid : ST0502G0, srchSbm : sub_ST0502G0R0, keyColumn : "OPER_DT", srchGrp : pgm_header, name :  "노선 목록"}
	 };				

	 var mainOptions = {
		EXL:scwin.btn_excel_onclick
	};

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.SINGLE_GRID_FORM, autoOptions, mainOptions);


	 var codeOptions = [{code : "FCLT_KIND", compID : "ST0502G1:INCDNT_TYPE"}
					];

	//코드옵션 실행	
	com.setCommonDtl(codeOptions, function(){
		com.searchGrid(ST0502G0, sub_ST0502G0R0);
	});

	com.executeSubmission(sub_ST0502SHI0);
	com.executeSubmission(sub_ST0502G0R0);

	var date = new Date();
	var year = date.getFullYear().toString();
	var month = (date.getMonth()+1).toString();
	var day = date.getDate().toString();
	var zero = 0;

	if(day.length == 1){
		day = zero + day;
	}

	if(month.length == 1){
		month = zero + month;
	}

	var today = year+month+day;
	var firstday = year+month+"01";
	F_DATE.setValue(firstday);
	L_DATE.setValue(today);	

};

var chart_ST0502 = null;

