};

scwin.btn_excel_onclick = function() {
	var excelOpt = {};  //excel download 옵션 
	excelOpt.fileName = "위반 노선별 통계.xls";    //또는 xlsx 확장자 사용 

	ST0502G1.pivotExcelDownload(excelOpt);

}

