};

scwin.ST0502G0_onrowindexchange = function(row,oldRow) {
	dma_sub_search.set("REP_ROUT_ID", dlt_BMS_ROUT_MST.getCellData(row, "REP_ROUT_ID"));
	com.executeSubmission(sub_ST0502G1R0);
	com.executeSubmission(sub_ST0502G2R0);
};


var CharData = function(){
	this.seriesname = {};
	this.fill = {};
	this.backgroundColor = {};
	this.borderColor = {};
	this.data = [];
}


scwin.drawChart = function(){
	debugger;
	var datasets = new Array();
	var transData = new Array();
	var i = 0;
	var j = 0;
	var num = 0;

	var rowCount = dlt_BRT_OPER_VIOLT_STAT2.getRowCount();
	var jsonData = dlt_BRT_OPER_VIOLT_STAT2.getAllJSON();


		var data = new Array();
		var xLabel = new Array();
		var color = new Array();
		var charData = new CharData();



		for(j; j<rowCount; j++){
			if(jsonData[j].FCLT_KIND ==  dlt_BRT_OPER_VIOLT_STAT2.getCellData(j, "FCLT_KIND")){
				transData.push(dlt_BRT_OPER_VIOLT_STAT2.getRowJSON(j));
			}else if(jsonData[num].FCLT_KIND  !=  dlt_BRT_OPER_VIOLT_STAT2.getCellData(j, "FCLT_KIND")){
				num = j;
				j = j;
				break;
			}
		}

		var sum1 = new Array();
		var sum2 = new Array();
		var sum3 = new Array();
		var labelJson = new Object();

		for(var y = 0; y < transData.length; y++){
			var label = transData[y].FCLT_KIND;
			var cond = transData[y].FCLT_COND;
			var incdntType = transData[y].FCLT_KIND;
			var sumCnt = transData[y].SUM_CNT;

			var showType = "";
			switch(incdntType){
				case "FK001":
					showType = "BIT";

					if (cond == "FC020"){
						sum1.push(sumCnt);
					}
					else if (cond == "FC021") {
						sum2.push(sumCnt);
					}
					else{
						sum3.push(sumCnt);
					}
					break;
				case "FK002":
					showType = "신호제어기";
					if (cond == "FC020"){
						sum1.push(sumCnt);
					}
					else if (cond == "FC021") {
						sum2.push(sumCnt);
					}
					else{
						sum3.push(sumCnt);
					}
					break;
				case "FK003":
					showType = "공조기";
					if (cond == "FC020"){
						sum1.push(sumCnt);
					}
					else if (cond == "FC021") {
						sum2.push(sumCnt);
					}
					else{
						sum3.push(sumCnt);
					}
					break;
				case "FK004":
					showType = "에어튜브";
					if (cond == "FC020"){
						sum1.push(sumCnt);
					}
					else if (cond == "FC021") {
						sum2.push(sumCnt);
					}
					else{
						sum3.push(sumCnt);
					}
					break;
				case "FK005":
					showType = "에어컨";
					if (cond == "FC020"){
						sum1.push(sumCnt);
					}
					else if (cond == "FC021") {
						sum2.push(sumCnt);
					}
					else{
						sum3.push(sumCnt);
					}
					break;
				}	


			var categoryJson = new Object();


			categoryJson = sumCnt;
			var isSame = false;
			for(i=0;i<xLabel.length;i++){
				if(xLabel[i].indexOf(showType)>=0){
					isSame = true;
					break;
				}
			}
			if(isSame==false)xLabel.push(showType);
			//labelJson = showType;

		};

			//data.push(labelJson);
			charData.data = data;
			datasets.push(charData);	
			transData = [];


	var ctx = document.getElementById('chart_ST0502');

	if(chart_ST0502 != null) {
		chart_ST0502.destroy();	
	}

	chart_ST0502 = new Chart(ctx, {
		    type: 'bar',
			  data: {
			    labels: xLabel,
			    datasets: [{
			      label: "정상",
			      type: "bar",
			      stack: "Base",
			      backgroundColor: "#eece01",
			      data: sum1,
			    }, {
			      label: "비정상",
			      type: "bar",
			      stack: "Base",
			      backgroundColor: "#87d84d",
			      data: sum2,
			    }, {
			      label: "네트워크 오류",
			      type: "bar",
			      stack: "Base",
			      backgroundColor: "#f8981f",      
			      data: sum3,
			    }]
			  },
			  options: {
			    scales: {
			      xAxes: [{
			        //stacked: true,
			        stacked: true,
			        ticks: {
			          beginAtZero: true,
			          maxRotation: 0,
			          minRotation: 0
			        }
			      }],
			      yAxes: [{
			        stacked: true,
			      }]
			    },
			  maintainAspectRatio: false
			  }
		});

}


scwin.TYPE10_onviewchange = function(info) {
	com.executeSubmission(sub_ST0502G0R0);
};

scwin.sbx_ChoiceBox_onviewchange = function(info) {		
	if (info.newValue == "ALL") {
		autoChoiceBox.hide();
		search_input.setValue("");
		search_input.show("");
	} else if (info.newValue == "REP_ROUT_NM") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_searchitem", "REP_ROUT_NM", "REP_ROUT_NM");
		autoChoiceBox.show("");
	} else if (info.newValue == "ROUT_NM") {
		search_input.hide();
		autoChoiceBox.setNodeSet("data:dlt_searchitem", "ROUT_NM", "ROUT_NM");
		autoChoiceBox.show("");
	} 
};	

scwin.ST0502G1_onrefresh = function(options) {
	var incdntLength = $(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel").length;
	var incdntLength2 = $(".w2editablePivot_contentTable_content_TR").find(".pvtRowLabel[colspan = 2]").length;

	for (var i=0; i<incdntLength; i++) {
		var incdntType = $(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel")[i].innerHTML;
		switch(incdntType.trim()) {
			case "FC020":
				showType = "정상";
				break;
			case "FC021":
				showType = "비정상";
				break;
			case "FC022":
				showType = "네트워크 오류";
				break;

		}
		$(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel")[i].innerHTML = showType;				
	}

	for (var i=0; i< incdntLength2; i++){
		var substitution = dlt_BRT_OPER_VIOLT_STAT.getCellData(i, "FCLT_KIND");
		switch(substitution){
			case "FK001":
				showType = "BIT";
				dlt_BRT_OPER_VIOLT_STAT.setCellData(i, "FCLT_KIND", showType);
				break;
			case "FK002":
				showType = "신호제어기";
				dlt_BRT_OPER_VIOLT_STAT.setCellData(i, "FCLT_KIND", showType);
				break;
			case "FK003":
				showType = "공조기";
				dlt_BRT_OPER_VIOLT_STAT.setCellData(i, "FCLT_KIND", showType);
				break;
			case "FK004":
				showType = "에어튜브";
				dlt_BRT_OPER_VIOLT_STAT.setCellData(i, "FCLT_KIND", showType);
				break;
			case "FK005":
				showType = "에어컨";
				dlt_BRT_OPER_VIOLT_STAT.setCellData(i, "FCLT_KIND", showType);
				break;
		}
	}
};


scwin.sub_ST0502G2R0_submitdone = function(e) {
	scwin.drawChart();		
};

scwin.F_DATE_onviewchange = function(info) {
	com.executeSubmission(sub_ST0502G1R0);
	var ldate = L_DATE.getValue();
	var fdate = F_DATE.getValue();

	if (fdate > ldate && ldate != "") {
		com.messagBox("alert", "지정할 수 없는 날짜입니다.", scwin.checkCloseF(info));
		com.executeSubmission(sub_ST0502G1R0);
	}
};

scwin.checkCloseF = function(info) {
	F_DATE.setValue(info.oldValue);
}

scwin.L_DATE_onviewchange = function(info) {
	com.executeSubmission(sub_ST0502G1R0);
	var ldate = L_DATE.getValue();
	var fdate = F_DATE.getValue();

	if (fdate > ldate && ldate != "") {
		com.messagBox("alert", "지정할 수 없는 날짜입니다.", scwin.checkCloseL(info));
		com.executeSubmission(sub_ST0502G1R0);
	}
};

scwin.checkCloseL = function(info) {
	L_DATE.setValue(info.oldValue);
}
