head.script:

	scwin.onpageload = function() {
		var autoOptions = {
			Main : { grid : ST0602G0, srchSbm : sub_ST0602G0R0, keyColumn : "REP_ROUT_ID", srchGrp : pgm_header, name :  "노선 목록"}
		 };				

		 var mainOptions = {
			EXL:scwin.btn_excel_onclick
		};

		com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.SINGLE_GRID_FORM, autoOptions, mainOptions);



		/*var subOptions2 = {
			SEARCH:[{cbFnc:scwin.openPopUpChart,nm:"노선별 비교 그래프 보기"}
			]
		}; 

		com.setSubBtn2(wfm_subBtn2, autoOptions, subOptions2);*/
		com.executeSubmission(sub_ST0602SHI0);
		com.executeSubmission(sub_ST0602SHI1);
		com.executeSubmission(sub_ST0602G0R0);

		var date = new Date();
		var year = date.getFullYear().toString();
		var month = (date.getMonth()+1).toString();
		var day = date.getDate().toString();
		var zero = 0;

		if(day.length == 1){
			day = zero + day;
		}

		if(month.length == 1){
			month = zero + month;
		}

		var today = year+month+day;
		var firstday = year+month+"01";
		F_DATE.setValue(firstday);
		L_DATE.setValue(today);	

	};

	var chart_ST0602 = null;

