	scwin.btn_excel_onclick = function() {
		var excelOpt = {};  //excel download 옵션 
		excelOpt.fileName = "위반 노선별 통계.xls";    //또는 xlsx 확장자 사용 

		ST0602G1.pivotExcelDownload(excelOpt);

	}

	scwin.sub_ST0602G0R0_submitdone = function(e) {
		com.setFocusedCell(ST0602G0, "ROUT_ID");
	};

	scwin.ST0602G0_onrowindexchange = function(row,oldRow) {
		dma_sub_search.set("REP_ROUT_ID", dlt_BMS_ROUT_MST.getCellData(row, "REP_ROUT_ID"));
		com.executeSubmission(sub_ST0602G1R0);
	};

	scwin.ST0602G0_oncellclick = function(row,col,colId) {
		if (col != "0"){
		   if(ST0602G0.getCellChecked(row, 'chk')==0){
		      ST0602G0.setCellChecked(row, 'chk', true);
		   }else if(ST0602G0.getCellChecked(row, 'chk')==1){
		      ST0602G0.setCellChecked(row, 'chk', false);
		   }
		}

		var chkJsonData = ST0602G0.getCheckedJSON("chk");
		var chkCnt = chkJsonData.length;
		var i = 0;
		var routId = [];

		//체크 가져오기
		for(i; i<chkCnt; i++){
			routId.push(chkJsonData[i].ROUT_ID);
		}

		dma_sub_search.set("ROUT_ID", routId);
		com.executeSubmission(sub_ST0602G1R0);
	};

	scwin.sub_ST0602G1R0_submitdone = function(e) {
		com.executeSubmission(sub_ST0602G2R0);

	};




	var CharData = function(){
		this.seriesname = {};
		this.fill = {};
		this.backgroundColor = {};
		this.borderColor = {};
		this.data = [];
	}


	scwin.drawChart = function(){
		var datasets = new Array();
		var transData = new Array();
		var i = 0;
		var j = 0;
		var num = 0;

		var rowCount = dlt_BRT_OPER_VIOLT_STAT2.getRowCount();
		var jsonData = dlt_BRT_OPER_VIOLT_STAT2.getAllJSON();

			var data = new Array();
			var xLabel = new Array();
			var color = new Array();
			var charData = new CharData();



			for(j; j<rowCount; j++){
				if(jsonData[j].VHC_ID ==  dlt_BRT_OPER_VIOLT_STAT2.getCellData(j, "VHC_ID")){
					transData.push(dlt_BRT_OPER_VIOLT_STAT2.getRowJSON(j));
				}else if(jsonData[num].VHC_ID  !=  dlt_BRT_OPER_VIOLT_STAT2.getCellData(j, "VHC_ID")){
					num = j;
					j = j;
					break;
				}
			}
				var sum1 = new Array();
				var sum2 = new Array();
				var sum3 = new Array();
				var labelJson = new Object();

			for(var y = 0; y < transData.length; y++){
			debugger;
				var cond = transData[y].DSPTCH_KIND;
				var label = transData[y].VHC_NO;
				var sumCnt = transData[y].SUM_CNT;
				//var incdntType = transData[y].VHC_ID;

				switch(cond){
					case "DK001":
					sum1.push(sumCnt);
					break;
					case "DK002":
					sum2.push(sumCnt);
					break;
					case "DK003":
					sum3.push(sumCnt);
					break;
				}


				var categoryJson = new Object();
				categoryJson = sumCnt;
				var isSame = false;
				for(i=0;i<xLabel.length;i++){
					if(xLabel[i].indexOf(label)>=0){
						isSame = true;
						break;
					}
				}
				if(isSame==false)xLabel.push(label);
				//labelJson = showType;
			};

				//data.push(labelJson);
				charData.data = data;
				datasets.push(charData);	
				transData = [];


		var ctx = document.getElementById('chart_ST0602');

		if(chart_ST0602 != null) {
			chart_ST0602.destroy();	
		}

		chart_ST0602 = new Chart(ctx, {
			    type: 'bar',
				  data: {
				    labels: xLabel,
				    datasets: [{
				      label: "1단계",
				      type: "bar",
				      stack: "Base",
				      backgroundColor: "#eece01",
				      data: sum1,
				    }, {
				      label: "2단계",
				      type: "bar",
				      stack: "Base",
				      backgroundColor: "#87d84d",
				      data: sum2,
				    }, {
				      label: "3단계",
				      type: "bar",
				      stack: "Base",
				      backgroundColor: "#f8981f",      
				      data: sum3,
				    }]
				  },
				  options: {
				    scales: {
				      xAxes: [{
				        //stacked: true,
				        stacked: true,
				        ticks: {
				          beginAtZero: true,
				          maxRotation: 0,
				          minRotation: 0
				        }
				      }],
				      yAxes: [{
				        stacked: true,
				      }]
				    },
				    maintainAspectRatio: false
				  }
			});

	}


