	scwin.ST0602G1_onrefresh = function(options) {
	debugger;
		var incdntLength = $("#mf_tac_layout_contents_MN00120502_body_ST0602G1").find(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel[rowspan = 2]").length;
		for (var i=0; i<incdntLength; i++) {
			var incdntType =  $("#mf_tac_layout_contents_MN00120502_body_ST0602G1").find(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel[rowspan = 2]")[i].innerHTML;
			switch(incdntType.trim()) {
				case "DK001":
					showType = "정상";
					break;
				case "DK002":
					showType = "가속";
					break;
				case "DK003":
					showType = "감속";
					break;
			}
			$("#mf_tac_layout_contents_MN00120502_body_ST0602G1").find(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel")[i].innerHTML = showType;				
		}

	};


