	scwin.onpageload = function() {
		var autoOptions = {
			Main : { grid : ST0603G0, srchSbm : sub_ST0603G0R0, keyColumn : "OPER_DT", srchGrp : pgm_header, name :  "노선 목록"}
		 };				

		 var mainOptions = {
			EXL:scwin.btn_excel_onclick
		};
		var codeOptions = [{code : "WAY_DIV", compID : "ST0603G0:WAY_DIV"}];
		com.setCommonDtl(codeOptions, function() {
			com.searchGrid(ST0603G0, sub_ST0603G0R0);
		});	
		com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.SINGLE_GRID_FORM, autoOptions, mainOptions);

		com.executeSubmission(sub_ST0603SHI0);
		com.executeSubmission(sub_ST0603SHI1);
		com.executeSubmission(sub_ST0603G0R0);

		var date = new Date();
		var year = date.getFullYear().toString();
		var month = (date.getMonth()+1).toString();
		var day = date.getDate().toString();

		var today;

		if (today < 9){
			today = year+month+"0"+day;
		} else{
			today = year+month+day;
		}


		var firstday = year+month+"01";
		F_DATE.setValue(firstday);
		L_DATE.setValue(today);	
		ST0603G0.mergeCells("byrestriccol");
	};

