	scwin.ST0603G1_onrefresh = function(options) {
		var incdntLength = $("#mf_tac_layout_contents_MN00120503_body_ST0603G1").find(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel").length;
		for (var i=0; i<incdntLength; i++) {
			var incdntType = $("#mf_tac_layout_contents_MN00120503_body_ST0603G1").find(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel")[i].innerHTML;
			switch(incdntType.trim()) {
				case "DK001":
					showType = "1단계";
					break;
				case "DK002":
					showType = "2단계";
					break;
				case "DK003":
					showType = "3단계";
					break;
			}
			$("#mf_tac_layout_contents_MN00120503_body_ST0603G1").find(".w2editablePivot_contentTable_header_TR").find(".pvtColLabel")[i].innerHTML = showType;				
		}

	};


