scwin.onpageload = function() {

	var autoOptions = {
		Main : { grid : ST0603G0, srchSbm : sub_ST0603G0R0, keyColumn : "OPER_DT", srchGrp : pgm_header, name :  "노선 목록"},
		Sub1 : { grid : ST0603G1, srchSbm : sub_ST0603G1R0, keyColumn : "VHC_ID", srchGrp : pgm_header, name :  "교차로 목록"}
	 };		

	  var mainOptions = {
         EXL:scwin.btn_excel_onclick
      };

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID_FORM, autoOptions, mainOptions);

	var date = new Date();
	var year = date.getFullYear().toString();
	var month = (date.getMonth()+1).toString();
	var day = date.getDate().toString();
	var zero = 0;

	if(day.length == 1){
		day = zero + day;
	}

	if(month.length == 1){
		month = zero + month;
	}

	var today = year+month+day;

	F_DATE.setValue(today);
	L_DATE.setValue(today);

	//텝 초기값 생성
	dma_sub_search.set("TAB_DIV_G1", "BUS");

	com.executeSubmission(sub_ST0603SHI0);

	$("#ST0603_TAB1").attr("style", "width:302px; margin-top: 10px;");

	//탭 목록 클릭 이벤트 - 그리드
	$(".moni_img").find("li").click( function() {
		var li_id = this.id;
		$(".moni_img").find("li").removeClass("on");
		$(".moni_img").find("#"+li_id).addClass("on");
		if(li_id == "li_bus") {
			dma_sub_search.set("TAB_DIV_G1", "BUS");
			com.executeSubmission(sub_ST0603G1R0);
			ST0603G1T0.show();
			ST0603G1T1.hide();
			ST0603G1T2.hide();
			if(dma_sub_search.get("SELECT_DIV") == 'TIME'){
				ST0603T0_vhcTime.show();
				ST0603T1_vhcDay.hide();
				ST0603T2_drvTime.hide();
				ST0603T3_drvDay.hide();
				ST0603T4_sttnTime.hide();
				ST0603T5_sttnDay.hide();
			}else if(dma_sub_search.get("SELECT_DIV") == ('DAY' || 'MONTH')){
				ST0603T0_vhcTime.hide();
				ST0603T1_vhcDay.show();
				ST0603T2_drvTime.hide();
				ST0603T3_drvDay.hide();
				ST0603T4_sttnTime.hide();
				ST0603T5_sttnDay.hide();
			}
		}
		else if(li_id == "li_drv") {
			dma_sub_search.set("TAB_DIV_G1", "DRV");
			com.executeSubmission(sub_ST0603G1R0);
			ST0603G1T1.show();
			ST0603G1T0.hide();
			ST0603G1T2.hide();
			if(dma_sub_search.get("SELECT_DIV") == 'TIME'){
				ST0603T0_vhcTime.hide();
				ST0603T1_vhcDay.hide();
				ST0603T2_drvTime.show();
				ST0603T3_drvDay.hide();
				ST0603T4_sttnTime.hide();
				ST0603T5_sttnDay.hide();
			}else if(dma_sub_search.get("SELECT_DIV") == ('DAY' || 'MONTH')){
				ST0603T0_vhcTime.hide();
				ST0603T1_vhcDay.hide();
				ST0603T2_drvTime.hide();
				ST0603T3_drvDay.show();
				ST0603T4_sttnTime.hide();
				ST0603T5_sttnDay.hide();
			}
		}
		else if(li_id == "li_sttn") {
			dma_sub_search.set("TAB_DIV_G1", "STTN");
			com.executeSubmission(sub_ST0603G1R0);
			ST0603G1T2.show();
			ST0603G1T1.hide();
			ST0603G1T0.hide();
			if(dma_sub_search.get("SELECT_DIV") == 'TIME'){
				ST0603T0_vhcTime.hide();
				ST0603T1_vhcDay.hide();
				ST0603T2_drvTime.hide();
				ST0603T3_drvDay.hide();
				ST0603T4_sttnTime.show();
				ST0603T5_sttnDay.hide();
			}else if(dma_sub_search.get("SELECT_DIV") == ('DAY' || 'MONTH')){
				ST0603T0_vhcTime.hide();
				ST0603T1_vhcDay.hide();
				ST0603T2_drvTime.hide();
				ST0603T3_drvDay.hide();
				ST0603T4_sttnTime.hide();
				ST0603T5_sttnDay.show();
			}
		}
	});

};

