//피봇 테이블 엑셀
scwin.btn_excel_onclick = function() {
	var excelOpt = {};  //excel download 옵션 
	excelOpt.fileName = "노선별 평균속도 통계.xls";    //또는 xlsx 확장자 사용 
	var gridTab = dma_sub_search.get('TAB_DIV_G1');
	var chartTab = SELECT_DIV.getValue();

	if(gridTab == 'BUS' && chartTab == 'TIME'){
		pivot_ST0603_VHC.pivotExcelDownload(excelOpt);
	}else if(gridTab == 'BUS' && (chartTab == 'DAY' || chartTab == 'MONTH')){
		pivot_ST0603_subVHC.pivotExcelDownload(excelOpt);
	}

	if(gridTab == 'DRV' && chartTab == 'TIME'){
		pivot_ST0603_DRV.pivotExcelDownload(excelOpt);
	}else if(gridTab == 'DRV' && (chartTab == 'DAY' || chartTab == 'MONTH')){
		pivot_ST0603_subDRV.pivotExcelDownload(excelOpt);
	}

	if(gridTab == 'STTN' && chartTab == 'TIME'){
		pivot_ST0603_STTN.pivotExcelDownload(excelOpt);
	}else if(gridTab == 'STTN' && (chartTab == 'DAY' || chartTab == 'MONTH')){
		pivot_ST0603_subSTTN.pivotExcelDownload(excelOpt);
	}

};

