		var chkCnt = chkJsonData.length;
		var i = 0;
		var drvId = [];

		com.setFocusedCell(ST0603G2, "DRV_ID");

		var date = new Date();
		var year = date.getFullYear();
		var month = date.getMonth()+1;
		var day = date.getDate();

		//체크 가져오기
		for(i; i<chkCnt; i++){
			drvId.push(chkJsonData[i].DRV_ID);
		}

		dma_sub_search.set("SELECT_DIV", SELECT_DIV.getValue());
		dma_sub_search.set("DRV_ID", drvId);

		com.executeSubmission(sub_ST0603G1R1);
