scwin.ST0603G1_oncellclick = function(row,col,colId) {
	if (col != "0"){
	   if(ST0603G1.getCellChecked(row, 'chk')==0){
	      ST0603G1.setCellChecked(row, 'chk', true);
	   }else if(ST0603G1.getCellChecked(row, 'chk')==1){
	      ST0603G1.setCellChecked(row, 'chk', false);
	   }
	}

	var chkJsonData = ST0603G1.getCheckedJSON("chk");
	var chkCnt = chkJsonData.length;
	var i = 0;
	var vhcId = [];

	//체크된 데이터 가져오기
	for(i; i<chkCnt; i++){
		vhcId.push(chkJsonData[i].VHC_ID);
	}

	dma_sub_search.set("VHC_ID", vhcId);
	dma_sub_search.set("SELECT_DIV", SELECT_DIV.getValue());

	if(chkJsonData.length != 0){
		com.executeSubmission(sub_ST0603G1R1);
		//pivot_ST0603.show();
	}else if(chkJsonData.length == 0){
		chart_ST0603.destroy();
		//pivot_ST0603.hide();
	}

};

