			//var statH = transData[y].STAT_H;
			var operDt = transData[y].OPER_DT;

			if(operDt != null){
				var categoryJson = new Object();
				var labelJson = new Object();

				categoryJson = val;
				labelJson = operDt;

				xLabel.push(labelJson);
				data.push(categoryJson);
			}

		};

	var rgb = scwin.getRandomColor();

	charData.data = data;
	charData.label = label;
	charData.borderColor = rgb;
	charData.backgroundColor = rgb;
	charData.borderWidth = 1; //선 굵기
	charData.pointBorderWidth = 1; //점 굵기
	charData.tension = 0;
	charData.pointRadius = 1; //점 지름
	charData.maxBarThickness= 60; //막대 굵기

	datasets.push(charData);

	transData = [];
}

	var ctx = document.getElementById('chart_ST0603');
	if(chart_ST0603 != null) {
	      chart_ST0603.destroy();   
	}

	chart_ST0603 = new Chart(ctx, {
		    type: 'bar',
			data: {
				labels:xLabel,
				datasets
			  },
		  options: {
		       // responsive: true
		        maintainAspectRatio: false,
		        scales: {
					yAxes: [{
							ticks: {
								beginAtZero: true,
								userCallback: function(label) {
									if(Math.floor(label) === label) {
									return label;	
									}	
								}	
							}
					}]
				}
		    }
		});

}

//바 차트 그리기 - 월별
scwin.DrawBarChart_Month = function(chkCnt){
	var datasets = new Array();
	var transData = new Array();
	var i = 0;
	var j = 0;
	var num = 0;

	var rowCount = dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getRowCount();
	var jsonData = dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getAllJSON();

	for(i; i<chkCnt; i++){
		var data = new Array();
		var xLabel = new Array();

		if(dma_sub_search.get("TAB_DIV_G1") == "BUS"){
			for(j; j<rowCount; j++){
				if(jsonData[num].VHC_ID ==  dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getCellData(j, "VHC_ID")){
					transData.push(dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getRowJSON(j));
				}else if(jsonData[num].VHC_ID  !=  dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getCellData(j, "VHC_ID")){
					num = j;
					j = j;
					break;
				}
			}

		}else if(dma_sub_search.get("TAB_DIV_G1") == "DRV"){
			for(j; j<rowCount; j++){
				if(jsonData[num].DRV_ID ==  dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getCellData(j, "DRV_ID")){
					transData.push(dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getRowJSON(j));
				}else if(jsonData[num].DRV_ID  !=  dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getCellData(j, "DRV_ID")){
					num = j;
					j = j;
					break;
				}
			}
		}else if(dma_sub_search.get("TAB_DIV_G1") == "STTN"){
			for(j; j<rowCount; j++){
				if(jsonData[num].NODE_ID ==  dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getCellData(j, "NODE_ID")){
					transData.push(dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getRowJSON(j));
				}else if(jsonData[num].NODE_ID  !=  dlt_BRT_OPER_PNCTLTY_STAT_PIVOT.getCellData(j, "NODE_ID")){
					num = j;
					j = j;
					break;
				}
			}
		}

		for(var y = 0; y < transData.length; y++){
			if(dma_sub_search.get("TAB_DIV_G1") == "BUS"){
				var label = transData[y].VHC_NO;
			}else if(dma_sub_search.get("TAB_DIV_G1") == "DRV"){
				var label = transData[y].DRV_NM;				
			}else if(dma_sub_search.get("TAB_DIV_G1") == "STTN"){
				var label = transData[y].NODE_NM;				
			}

			var val = transData[y].CHG_OPER_CNT;
			//var statH = transData[y].STAT_H;
			var operDt = transData[y].OPER_DT;

			if(operDt != null){
				var categoryJson = new Object();
				var labelJson = new Object();

				categoryJson = val;
				labelJson = operDt;

				xLabel.push(labelJson);
				data.push(categoryJson);
			}

		};
	var charData = new CharData();
	var rgb = scwin.getRandomColor();

	charData.data = data;
	charData.label = label;
	charData.borderColor = rgb;
	charData.backgroundColor = rgb;
	charData.borderWidth = 1; //선 굵기
	charData.pointBorderWidth = 1; //점 굵기
	charData.tension = 0;
	charData.pointRadius = 1; //점 지름
	charData.maxBarThickness= 60; //막대 굵기

	datasets.push(charData);

	transData = [];
}

	if(chart_ST0603 != null) {
	      chart_ST0603.destroy();   
	}

	var ctx = document.getElementById('chart_ST0603');
	chart_ST0603 = new Chart(ctx, {
		    type: 'bar',
			data: {
				labels:xLabel,
				datasets
			  },
		  options: {
		       // responsive: true
		        maintainAspectRatio: false
		    }
		});

}


scwin.TYPE10_onviewchange = function(info) {
	com.searchGrid(ST0603G0, sub_ST0603G0R0);
};

scwin.F_DATE_onviewchange = function(info) {
	var ldate = L_DATE.getValue();
	var fdate = F_DATE.getValue();

	if (fdate > ldate && ldate != "") {
		com.messagBox("alert", "지정할 수 없는 날짜입니다.", scwin.checkCloseF(info));
	}

	com.executeSubmission(sub_ST0603G1R1);
};
scwin.checkCloseF = function(info) {
	F_DATE.setValue(info.oldValue);
}

scwin.L_DATE_onviewchange = function(info) {
	var ldate = L_DATE.getValue();
	var fdate = F_DATE.getValue();

	if (fdate > ldate && ldate != "") {
		com.messagBox("alert", "지정할 수 없는 날짜입니다.", scwin.checkCloseL(info));
	}

	com.executeSubmission(sub_ST0603G1R1);
};

scwin.checkCloseL = function(info) {
	L_DATE.setValue(info.oldValue);
}


scwin.SELECT_DIV_onviewchange = function(info) {
	dma_sub_search.set("SELECT_DIV", info.newValue);


	if(info.newValue == 'MONTH'){
		F_DATE.setCalendarValueType("yearMonth");
		L_DATE.setCalendarValueType("yearMonth");
	}else{
		F_DATE.setCalendarValueType("yearMonthDate");
		L_DATE.setCalendarValueType("yearMonthDate");
	}

	com.executeSubmission(sub_ST0603G1R1);
};

