scwin.onpageload = function() {

	var autoOptions = {
		Main : { grid : ST0604G0, srchSbm : sub_ST0604G0R0, keyColumn : "OPER_DT", srchGrp : pgm_header, name :  "노선 목록"},
		Sub1 : { grid : ST0604G1, srchSbm : sub_ST0604G1R0, keyColumn : "NODE_ID", srchGrp : pgm_header, name :  "교차로 목록"}
	 };		

	  var mainOptions = {
         EXL:scwin.btn_excel_onclick
      };

    dma_sub_search.set("SELECT_DIV", "TIME");

	com.setMainBtn2(wfm_mainBtn, gcm.DISP_TYPE.DUAL_GRID_FORM, autoOptions, mainOptions);

	var date = new Date();
	var year = date.getFullYear().toString();
	var month = (date.getMonth()+1).toString();
	var day = date.getDate().toString();
	var zero = 0;

	if(day.length == 1){
		day = zero + day;
	}

	if(month.length == 1){
		month = zero + month;
	}

	var today = year+month+day;

	F_DATE.setValue(today);
	L_DATE.setValue(today);

	com.executeSubmission(sub_ST0604SHI0);

};

scwin.onpageunload = function() {

};

